import React from 'react';
import './ProductCard.css'; // Add a separate CSS file for styling the product cards

const ProductCard = ({ product }) => {
  return (
    <div className="product-card">
      <div className="product-image-container">
        <img src={product.imageUrl} alt={product.name} className="product-image" />
        <div className="action-icons">
          <button className="icon-btn wishlist-btn">
            <i className="fa fa-heart"></i> {/* Heart icon for wishlist */}
          </button>
          <button className="icon-btn search-btn">
            <i className="fa fa-search"></i> {/* Search icon for zoom */}
          </button>
        </div>
      </div>
      <div className="product-info">
        <h3 className="product-name">{product.name}</h3>
        <p className="product-price">Rs. {product.price}</p>
        <button className="add-to-cart-btn">Add to Cart</button> {/* Add to cart button */}
      </div>
    </div>
  );
};

export default ProductCard;
