// src/pages/ProductPage.js
import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import './ProductPage.css'; // Import CSS for styling

const ProductPage = () => {
  const { productId } = useParams();

  const productDetails = {
    1: {
      name: 'Air Shot Lotus',
      description: 'A refreshing floral fragrance that embodies the spirit of nature.',
      price: 49.99,
      sizes: ['50ml', '100ml', '150ml'],
      images: [
        '/air-shot-lotus.jpg', // Updated image paths
        '/air-shot-lotus-2.jpg',
        '/air-shot-lotus-3.jpg',
      ],
      reviews: [
        { id: 1, username: 'John', rating: 5, comment: 'Absolutely love this scent!' },
        { id: 2, username: 'Jane', rating: 4, comment: 'It lasts all day, highly recommend!' },
      ],
    },
    2: {
      name: 'Air Shot Sweet',
      description: 'A sweet and enchanting fragrance.',
      price: 59.99,
      sizes: ['50ml', '100ml', '150ml'],
      images: [
        '/air-shot-sweet.jpg', // Updated image paths
        '/air-shot-sweet-2.jpg',
        '/air-shot-sweet-3.jpg',
      ],
      reviews: [
        { id: 3, username: 'Alice', rating: 5, comment: 'Such a delightful fragrance!' },
        { id: 4, username: 'Bob', rating: 3, comment: 'It\'s nice, but not my favorite.' },
      ],
    },
    3: {
      name: 'Air Shot Romance',
      description: 'A romantic and captivating fragrance.',
      price: 69.99,
      sizes: ['50ml', '100ml', '150ml'],
      images: [
        '/air-shot-romance.jpg', // Updated image paths
        '/air-shot-romance-2.jpg',
        '/air-shot-romance-3.jpg',
      ],
      reviews: [
        { id: 5, username: 'Charlie', rating: 4, comment: 'A lovely scent for special occasions.' },
        { id: 6, username: 'Daisy', rating: 5, comment: 'My favorite fragrance of all time!' },
      ],
    },
    // Add other products here
  };

  const product = productDetails[productId];

  // State for the new review
  const [newReview, setNewReview] = useState({ username: '', rating: 1, comment: '' });

  const handleReviewSubmit = (e) => {
    e.preventDefault();
    const updatedReviews = [...product.reviews, { ...newReview, id: product.reviews.length + 1 }];
    product.reviews = updatedReviews; // Update the reviews
    setNewReview({ username: '', rating: 1, comment: '' }); // Reset the form
  };

  // Calculate the average rating
  const averageRating = () => {
    const totalRating = product.reviews.reduce((acc, review) => acc + review.rating, 0);
    return (totalRating / product.reviews.length).toFixed(1);
  };

  return (
    <div className="product-page">
      {product ? (
        <div className="product-container">
          <h1 className="product-title">{product.name}</h1>
          <div className="product-gallery">
            {product.images.map((img, index) => (
              <img key={index} src={img} alt={`${product.name} ${index + 1}`} className="product-image"/>
            ))}
          </div>
          <p className="product-description">{product.description}</p>
          <p className="product-price">Price: ${product.price.toFixed(2)}</p>
          <p className="product-sizes">Available Sizes: {product.sizes.join(', ')}</p>

          {/* Average Rating */}
          <div className="average-rating">
            <p>Rating: {averageRating()} / 5</p>
            <div className="stars">
              {[...Array(Math.floor(averageRating()))].map((_, i) => (
                <span key={i} className="star">⭐</span>
              ))}
              {averageRating() % 1 !== 0 && <span className="star">⭐</span>} {/* For half star */}
            </div>
          </div>

          {/* Buttons for Add to Cart and Buy Now */}
          <div className="product-buttons">
            <button className="add-to-cart-btn">Add to Cart</button>
            <button className="buy-now-btn">Buy Now</button>
          </div>

          <h2 className="reviews-title">Reviews</h2>
          <div className="reviews-section">
            {product.reviews.map((review) => (
              <div key={review.id} className="review-card">
                <h3 className="review-username">{review.username}</h3>
                <div className="stars">
                  {[...Array(review.rating)].map((_, i) => (
                    <span key={i} className="star">⭐</span>
                  ))}
                </div>
                <p className="review-comment">{review.comment}</p>
              </div>
            ))}
          </div>

          <h3 className="add-review-title">Add a Review</h3>
          <form onSubmit={handleReviewSubmit} className="review-form">
            <input
              type="text"
              placeholder="Your Name"
              value={newReview.username}
              onChange={(e) => setNewReview({ ...newReview, username: e.target.value })}
              required
              className="review-input"
            />
            <select
              value={newReview.rating}
              onChange={(e) => setNewReview({ ...newReview, rating: Number(e.target.value) })}
              required
              className="review-input"
            >
              <option value="1">1 Star</option>
              <option value="2">2 Stars</option>
              <option value="3">3 Stars</option>
              <option value="4">4 Stars</option>
              <option value="5">5 Stars</option>
            </select>
            <textarea
              placeholder="Your Review"
              value={newReview.comment}
              onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
              required
              className="review-textarea"
            />
            <button type="submit" className="submit-review-btn">Submit Review</button>
          </form>
        </div>
      ) : (
        <p>Product not found.</p>
      )}
    </div>
  );
};

export default ProductPage;
