// Navbar.js

import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import { FaUser, FaShoppingCart } from 'react-icons/fa';

const Navbar = () => {
    const [isDropdownOpen, setDropdownOpen] = useState(false);

    const toggleDropdown = () => {
        setDropdownOpen(!isDropdownOpen);
    };

    return (
        <nav className="navbar">
            {/* Logo */}
            <div className="logo">
                <Link to="/">
                    <img src="/path-to-your-logo.png" alt="Logo" width="150" />
                </Link>
            </div>

            {/* Centered Nav Links */}
            <ul className="nav-links">
                <li><Link to="/women">Women</Link></li>
                <li><Link to="/men">Men</Link></li>
                <li><Link to="/special-combos">Special Combos</Link></li>
                <li><Link to="/gift-sets">Gift Sets</Link></li>
                <li><Link to="/other-products">Other Products</Link></li>
                <li><Link to="/about">About</Link></li>
            </ul>

            {/* Right Side Icons */}
            <div className="right-icons">
                {/* Account Dropdown */}
                <div className="account-dropdown">
                    <FaUser className="icon" onClick={toggleDropdown} />
                    {isDropdownOpen && (
                        <div className="dropdown-menu">
                            <Link to="/profile" className="dropdown-item">Profile</Link>
                            <Link to="/wallet" className="dropdown-item">Wallet</Link>
                            <Link to="/promo-code" className="dropdown-item">Promo Code</Link>
                            <Link to="/history" className="dropdown-item">History of Purchases</Link>
                        </div>
                    )}
                </div>
                <FaShoppingCart className="icon" />
            </div>
        </nav>
    );
};

export default Navbar;
