// src/components/HomePage.js
import React from 'react';
import { useHistory } from 'react-router-dom';
import Navbar from '../components/Navbar';
import Banner from '../components/Banner';
import ProductCard from '../components/ProductCard';
import Footer from '../components/Footer';
import '../App.css';
import './HomePage.css';

const HomePage = () => {
  const history = useHistory();

  // Products array with sample data
  const products = [
    { id: 1, name: 'Air Shot Lotus', price: 169, imageUrl: 'air-shot-lotus.jpg', ingredients: 'Lotus, Water', features: 'Refreshing scent' },
    { id: 2, name: 'Air Shot Sweet', price: 199, imageUrl: 'air-shot-sweet.jpg', ingredients: 'Vanilla, Almond', features: 'Sweet and subtle' },
    // Add other products here
  ];

  const handleProductClick = (productId) => {
    history.push(`/product/${productId}`);
  };

  return (
    <div className="homepage">
      <Navbar />
      <Banner />
      <section className="products-section">
        <h2>Featured Perfumes</h2>
        <div className="products-grid">
          {products.map((product) => (
            <ProductCard key={product.id} product={product} onClick={() => handleProductClick(product.id)} />
          ))}
        </div>
      </section>
      <Footer />
    </div>
  );
};

export default HomePage;
