import React from 'react';
import { useNavigate } from 'react-router-dom';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const navigate = useNavigate();

  const viewDetails = () => {
    navigate(`/product/${product.id}`);
  };

  const imageUrl = `/images/${product.imageUrl}`;
  console.log('Image URL:', imageUrl); // Log the image URL for debugging

  return (
    <div className="product-card" onClick={viewDetails}>
      <div className="product-image-container">
        <img
          src={imageUrl}
          alt={product.name}
          className="product-image"
          onError={(e) => {
            e.target.onerror = null; // Prevent infinite loop
            e.target.src = "/images/default.jpg"; // Fallback image
          }}
        />
        <div className="action-icons">
          <button className="wishlist-icon" aria-label="Add to Wishlist">❤️</button>
          <button className="search-icon" aria-label="View Details"></button>
        </div>
      </div>
      <h3 className="product-name">{product.name}</h3>
      <p className="product-price">Rs. {product.price}</p>
      <button className="add-to-cart-btn">Add to Cart</button>
    </div>
  );
};

export default ProductCard;
