import React, { useState, useEffect } from 'react';
import StarRatings from 'react-star-ratings';
import ReviewForm from './ReviewForm';
import './ProductPage.css';

const ProductPage = () => {
  const [product, setProduct] = useState(null);
  const [reviews, setReviews] = useState([]);
  const productId = window.location.pathname.split('/').pop(); // Get product ID from the URL

 
    
    useEffect(() => {
      const fetchProducts = async () => {
          try {
              const response = await fetch('http://localhost:5000/api/products');
              if (!response.ok) throw new Error('Network response was not ok');
              const data = await response.json();
              setProducts(data);
          } catch (error) {
              console.error('Failed to fetch products:', error);
          }
      };

      // Fetch recommended products as well
      const fetchAdditionalProducts = async () => {
          // Assuming you have an endpoint for additional products
          const response = await fetch('http://localhost:5000/api/recommended-products');
          const data = await response.json();
          setAdditionalProducts(data);
      };

      fetchProducts();
      fetchAdditionalProducts();
  }, []);
   
  // Define the handleReviewSubmitted function
  const handleReviewSubmitted = (newReview) => {
    setReviews((prevReviews) => [...prevReviews, newReview]);
  };

  if (!product) {
    return <div>Loading...</div>; // Loading state
  }

  return (
    <div className="product-page">
      <div className="product-info">
        <img
          src={`/images/${product.imageUrl}`} // Ensure correct path to image
          alt={product.name}
          className="product-image"
        />
        <div className="product-details">
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <p className="product-price">Rs. {product.price.toFixed(2)}</p>
          <StarRatings
            rating={product.rating} // Assuming the product object has a rating property
            starRatedColor="gold"
            numberOfStars={5}
            name="rating"
            starDimension="20px"
            starSpacing="3px"
          />
          <button className="add-to-cart-btn">Add to Cart</button>
          <button className="buy-now-btn">Buy Now</button>
        </div>
      </div>

      <h2 className="review-title">Customer Reviews</h2>
      <div className="reviews-section">
        {reviews.length > 0 ? (
          reviews.map((review) => (
            <div key={review._id} className="review-card">
              <h4>{review.userId.name || 'Anonymous'}</h4>
              <StarRatings
                rating={review.rating}
                starRatedColor="gold"
                numberOfStars={5}
                name="rating"
                starDimension="15px"
                starSpacing="2px"
              />
              <p>{review.comment}</p>
            </div>
          ))
        ) : (
          <p>No reviews yet. Be the first to leave a review!</p>
        )}
      </div>

      <ReviewForm productId={product._id} onReviewSubmitted={handleReviewSubmitted} />
    </div>
  );
};

export default ProductPage;
