import React, { useState, useEffect } from 'react';
import StarRatings from 'react-star-ratings';
import { FacebookShareButton, TwitterShareButton, WhatsappShareButton } from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp, FaShareAlt } from 'react-icons/fa';
import { useParams } from 'react-router-dom';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import './ProductPage.css';

const ProductPage = () => {
  const { id } = useParams(); // Get product ID from URL
  const [product, setProduct] = useState(null); // State to hold product data
  const [loading, setLoading] = useState(true); // Loading state

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`/api/products/${id}`); // Fetch product by ID
        if (!response.ok) throw new Error('Failed to fetch product');
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error('Error fetching product:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  if (loading) {
    return <div>Loading...</div>; // Handle loading state
  }

  if (!product) {
    return <div>Product not found.</div>; // Handle product not found
  }

  return (
    <div className="product-page">
      <Navbar />
      <div className="product-details">
        <img src={`http://localhost:5000${product.imageUrl}`} alt={product.name} className="product-image" />
        <div className="product-info">
          <h2>{product.name}</h2>
          <p className="product-price">Rs. {product.price}</p>
          <StarRatings
            rating={product.rating}
            starRatedColor="gold"
            numberOfStars={5}
            name="rating"
          />
          <p>{product.description}</p>
          <div className="share-buttons">
            <h4>Share:</h4>
            <FacebookShareButton url={`http://localhost:3000/product/${product._id}`}><FaFacebook /></FacebookShareButton>
            <TwitterShareButton url={`http://localhost:3000/product/${product._id}`}><FaTwitter /></TwitterShareButton>
            <WhatsappShareButton url={`http://localhost:3000/product/${product._id}`}><FaWhatsapp /></WhatsappShareButton>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default ProductPage;
