import React, { useState } from 'react';
import StarRatings from 'react-star-ratings';

const ReviewForm = ({ onSubmit, submissionMessage, isError }) => {
    const [newReview, setNewReview] = useState({
        username: '',
        rating: 0,
        comment: '',
    });

    const handleReviewSubmit = (e) => {
        e.preventDefault();
        onSubmit(newReview);
        setNewReview({ username: '', rating: 0, comment: '' });
    };

    return (
        <div>
            <form className="review-form" onSubmit={handleReviewSubmit}>
                <h2>Leave a Review</h2>
                <input
                    type="text"
                    placeholder="Your Name"
                    value={newReview.username}
                    onChange={(e) => setNewReview({ ...newReview, username: e.target.value })}
                    required
                />
                <StarRatings
                    rating={newReview.rating}
                    starRatedColor="gold"
                    numberOfStars={5}
                    name="rating"
                    starDimension="20px"
                    starSpacing="3px"
                    changeRating={(newRating) => setNewReview({ ...newReview, rating: newRating })}
                />
                <textarea
                    placeholder="Your Review"
                    value={newReview.comment}
                    onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
                    required
                />
                <button type="submit" className="submit-review-btn">Submit Review</button>
            </form>
            {submissionMessage && <p className={isError ? 'error-message' : 'success-message'}>{submissionMessage}</p>}
        </div>
    );
};

export default ReviewForm;
