import React from 'react';
import { useNavigate } from 'react-router-dom';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const navigate = useNavigate();

  const viewDetails = () => {
    navigate(`/product/${product._id}`); // Use product._id for MongoDB compatibility
  };

  return (
    <div className="product-card" onClick={viewDetails}>
      <div className="product-image-container">
        <img
          src={`http://localhost:5000${product.imageUrl}`} // Adjust to match the server path
          alt={product.name}
          className="product-image"
        />
        <div className="action-icons">
          <button className="wishlist-icon" aria-label="Add to Wishlist">‚ù§Ô∏è</button>
          <button className="search-icon" aria-label="View Details">üîç</button>
        </div>
      </div>
      <h3 className="product-name">{product.name}</h3>
      <p className="product-price">Rs. {product.price}</p>
      <button className="add-to-cart-btn">Add to Cart</button>
    </div>
  );
};

export default ProductCard;
