import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import Banner from '../components/Banner';
import ProductCard from '../components/ProductCard';
import '../App.css';
import './HomePage.css'; // Import the new CSS file for styling
import Footer from '../components/footer';
import { useHistory } from 'react-router-dom';
const HomePage = () => {
  // Define the products array directly inside the component for now
  const history = useHistory();

  const products = [
     {
      id: 1,
      name: 'Air Shot Lotus',
      price: 169,
      imageUrl: 'air-shot-lotus.jpg' // Matches the file name in the images folder
    },
    {
      id: 2,
      name: 'Air Shot Sweet',
      price: 199,
      imageUrl: 'air-shot-sweet.jpg'
    },
    {
      id: 3,
      name: 'Air Shot Romance',
      price: 199,
      imageUrl: 'air-shot-romance.jpg'
    },
    {
      id: 4,
      name: 'Air Shot Oud',
      price: 199,
      imageUrl: 'air-shot-oud.jpg'
    },  {
      id: 5,
      name: 'Air Shot Lotus',
      price: 169,
      imageUrl: 'air-shot-lotus.jpg' // Matches the file name in the images folder
    },
    {
      id: 6,
      name: 'Air Shot Sweet',
      price: 199,
      imageUrl: 'air-shot-sweet.jpg'
    },
    {
      id: 7,
      name: 'Air Shot Romance',
      price: 199,
      imageUrl: 'air-shot-romance.jpg'
    },
    {
      id:8,
      name: 'Air Shot Oud',
      price: 199,
      imageUrl: 'air-shot-oud.jpg'
    }
  ];

  const handleProductClick = (productId) => {
    history.push(`/product/${productId}`);
  };
  return (
    <div className="homepage">
      <Navbar />
      <Banner /> {/* Auto-changing banner */}
      <section className="products-section">
        <h2>Features Perfume </h2>
        <div className="products-grid"> {/* Container for product cards */}
          {products.length > 0 ? (
            products.map(product => (
              <ProductCard key={product.id} product={product} />
            ))
          ) : (
            <p>No products available.</p>
          )}
        </div>
        <Footer />
      </section>
    </div>
  );
};

export default HomePage;
