import React, { useState, useEffect, useRef } from 'react';
import StarRatings from 'react-star-ratings';
import {
  FacebookShareButton,
  TwitterShareButton,
  WhatsappShareButton,
} from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp, FaShareAlt } from 'react-icons/fa';
import ReviewForm from './ReviewForm'; // Import ReviewForm
import './ProductPage.css';

const product = {
  id: 1,
  name: 'Elegant Perfume',
  description: 'Experience elegance in every spritz with this luxurious fragrance.',
  price: 49.99,
  rating: 4.5,
  image: '/images/elegant-perfume.jpg',
  variants: [
    { id: 1.1, name: 'Elegant Perfume - 50ml', price: 29.99, image: '/images/elegant-perfume-50ml.jpg' },
    { id: 1.2, name: 'Elegant Perfume - 100ml', price: 49.99, image: '/images/elegant-perfume-100ml.jpg' },
    { id: 1.3, name: 'Elegant Perfume - Limited Edition', price: 69.99, image: '/images/elegant-perfume-limited.jpg' },
  ],
};

const additionalProducts = [
  { id: 5, name: 'Fresh Citrus Perfume', price: 39.99, image: '/images/fresh-citrus-perfume.jpg' },
  { id: 6, name: 'Sweet Blossom Perfume', price: 45.99, image: '/images/sweet-blossom-perfume.jpg' },
  { id: 7, name: 'Classic Wood Perfume', price: 49.99, image: '/images/classic-wood-perfume.jpg' },
];

const ProductPage = () => {
  const [showShareOptions, setShowShareOptions] = useState(false);
  const [showCollection, setShowCollection] = useState(false);
  const [reviews, setReviews] = useState({}); // Store reviews by product ID
  const [submissionMessage, setSubmissionMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const collectionRef = useRef(null);
  const shareUrl = window.location.href;

  const handleReviewSubmit = (productId, review) => {
    setReviews((prevReviews) => ({
      ...prevReviews,
      [productId]: prevReviews[productId] ? [...prevReviews[productId], review] : [review],
    }));

    setSubmissionMessage('Review submitted successfully!');
    setIsError(false);
  };

  return (
    <div className="product-page">
      <div className="product-info">
        <img src={product.image} alt={product.name} className="product-image" />
        <div className="product-details">
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <div className="price-rating">
            <p className="product-price">${product.price.toFixed(2)}</p>
            <div className="product-rating">
              <StarRatings
                rating={product.rating}
                starRatedColor="gold"
                numberOfStars={5}
                name="rating"
                starDimension="20px"
                starSpacing="3px"
              />
              <span>{product.rating} / 5</span>
            </div>
          </div>
          <button className="add-to-cart-btn">Add to Cart</button>
          <button className="buy-now-btn">Buy Now</button>
        </div>
      </div>

      {/* Recommended Products Section */}
      <div className="recommended-products">
        <h2>Recommended Products</h2>
        <div className="recommended-products-grid">
          {additionalProducts.map((prod) => (
            <div key={prod.id} className="recommended-product">
              <img src={prod.image} alt={prod.name} className="recommended-product-image" />
              <h3>{prod.name}</h3>
              <p>${prod.price.toFixed(2)}</p>
              <button className="view-product-btn">View Product</button>
            </div>
          ))}
        </div>
      </div>

      {/* Review Section */}
      <div key={product.id} className="product-container">
        <ReviewForm onSubmit={(review) => handleReviewSubmit(product.id, review)} submissionMessage={submissionMessage} isError={isError} />
        <div>
          <h2>Customer Reviews</h2>
          {reviews[product.id] && reviews[product.id].map((review, index) => (
            <div className="review" key={index}>
              <h4>{review.username}</h4>
              <div className="stars">{'★'.repeat(review.rating) + '☆'.repeat(5 - review.rating)}</div>
              <p>{review.comment}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
