import React, { useState, useEffect } from 'react';
import StarRatings from 'react-star-ratings';
import { FacebookShareButton, TwitterShareButton, WhatsappShareButton } from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp, FaShareAlt } from 'react-icons/fa';
import { handleError } from './utils/errorHandler'; // Import the error handler
import './ProductPage.css';

const ProductPage = () => {
  const [product, setProduct] = useState(null);
  const [showShareOptions, setShowShareOptions] = useState(false);
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({ username: '', rating: 1, comment: '' });
  const [submissionMessage, setSubmissionMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');
  const [additionalProducts, setAdditionalProducts] = useState([]);
  const productId = window.location.pathname.split('/').pop();
  const shareUrl = window.location.href;

  const toggleShareOptions = () => {
    setShowShareOptions((prev) => !prev);
  };

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/products/${productId}`);
        if (!response.ok) throw new Error('Failed to fetch product');
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        handleError(error, setIsError, setErrorMessage);
      }
    };

    const fetchReviews = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/reviews/${productId}`);
        if (!response.ok) throw new Error('Failed to fetch reviews');
        const data = await response.json();
        setReviews(data);
      } catch (error) {
        handleError(error, setIsError, setErrorMessage);
      }
    };

    const fetchAdditionalProducts = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/products/recommended`);
        if (!response.ok) throw new Error('Failed to fetch recommended products');
        const data = await response.json();
        setAdditionalProducts(data);
      } catch (error) {
        handleError(error, setIsError, setErrorMessage);
      }
    };

    fetchProduct();
    fetchReviews();
    fetchAdditionalProducts();
  }, [productId]);

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`http://localhost:5000/api/reviews/${productId}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newReview),
      });
      if (!response.ok) throw new Error('Failed to submit review');
      const addedReview = await response.json();
      setReviews((prevReviews) => [...prevReviews, addedReview]);
      setNewReview({ username: '', rating: 1, comment: '' });
      setSubmissionMessage('Review submitted successfully!');
      setIsError(false);
    } catch (error) {
      handleError(error, setIsError, setErrorMessage);
      setSubmissionMessage('Failed to submit review: Please try again.');
    } finally {
      setTimeout(() => setSubmissionMessage(''), 3000);
    }
  };

  if (isError) return <div className="error-message">{errorMessage}</div>;
  if (!product) return <div>Loading...</div>;

  return (
    <div className="product-page">
      <div className="product-info">
        <img src={`http://localhost:5000${product.image}`} alt={product.name} className="product-image" />
        <div className="product-details">
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <div className="price-rating">
            <p className="product-price">${product.price.toFixed(2)}</p>
            <div className="product-rating">
              <StarRatings rating={product.rating} starRatedColor="gold" numberOfStars={5} name="rating" starDimension="20px" starSpacing="3px" />
              <span>{product.rating} / 5</span>
            </div>
          </div>
          <button className="add-to-cart-btn">Add to Cart</button>
          <button className="buy-now-btn">Buy Now</button>
          <div className="share-container">
            <button className="share-btn" onClick={toggleShareOptions}>
              <FaShareAlt size={20} /> Share
            </button>
            {showShareOptions && (
              <div className="share-dropdown">
                <h4>Share via</h4>
                <FacebookShareButton url={shareUrl}><FaFacebook size={20} /> Facebook</FacebookShareButton>
                <TwitterShareButton url={shareUrl}><FaTwitter size={20} /> Twitter</TwitterShareButton>
                <WhatsappShareButton url={shareUrl}><FaWhatsapp size={20} /> WhatsApp</WhatsappShareButton>
              </div>
            )}
          </div>
        </div>
      </div>
      <div className="review-section">
        <h2>Reviews</h2>
        {reviews.map((review, index) => (
          <div key={index} className="review">
            <p><strong>{review.username}</strong> rated it {review.rating} / 5</p>
            <p>{review.comment}</p>
          </div>
        ))}
        <form onSubmit={handleReviewSubmit}>
          <input type="text" value={newReview.username} onChange={(e) => setNewReview({ ...newReview, username: e.target.value })} placeholder="Your name" required />
          <select value={newReview.rating} onChange={(e) => setNewReview({ ...newReview, rating: e.target.value })}>
            {[1, 2, 3, 4, 5].map((rate) => (
              <option key={rate} value={rate}>{rate}</option>
            ))}
          </select>
          <textarea value={newReview.comment} onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })} placeholder="Your review" required />
          <button type="submit">Submit Review</button>
        </form>
        {submissionMessage && <div className={`submission-message ${isError ? 'error' : 'success'}`}>{submissionMessage}</div>}
      </div>
      <div className="recommended-products">
        <h2>Recommended Products</h2>
        {additionalProducts.map((additionalProduct) => (
          <div key={additionalProduct._id} className="recommended-product">
            <img src={`http://localhost:5000${additionalProduct.image}`} alt={additionalProduct.name} />
            <p>{additionalProduct.name}</p>
            <p>${additionalProduct.price.toFixed(2)}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductPage;
