import React from 'react';
import { useNavigate } from 'react-router-dom';
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const navigate = useNavigate();

  // Function to handle navigation to product details page
  const viewDetails = () => {
    console.log('Navigating to product:', product); // Log product for debugging
    navigate(`/product/${product._id}`); // Navigate to product page with product ID
  };

  return (
    <div className="product-card" onClick={viewDetails}>
      <div className="product-image-container">
        <img
          src={`/images/${product.imageUrl}`} // Ensure this path is correct
          alt={product.name}
          className="product-image"
          onError={(e) => {
            e.target.onerror = null; // Prevent infinite loop
            e.target.src = "/images/default.jpg"; // Fallback image
          }}
        />
        <div className="action-icons">
          <button className="wishlist-icon" aria-label="Add to Wishlist">
            ‚ù§Ô∏è
          </button>
          <button className="search-icon" aria-label="View Details" onClick={viewDetails}>
            üîç
          </button>
        </div>
      </div>
      <h3 className="product-name">{product.name}</h3>
      <p className="product-price">Rs. {product.price.toFixed(2)}</p>
      <button className="add-to-cart-btn">Add to Cart</button>
    </div>
  );
};

export default ProductCard;
