// src/components/Signup.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
//import { auth, provider } from '../firebase'; // Import auth and provider
//import { signInWithPopup } from 'firebase/auth';
import './Signup.css';

const Signup = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    // Here you would typically handle signup logic, e.g., sending data to a server
    console.log('Signing up:', { username, email, password });
    // Navigate to home page or login page after signup
    navigate('/');
  };

  const handleGoogleSignIn = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;
      console.log('Google sign-in successful!', user);
      // Navigate to home or another page
      navigate('/');
    } catch (error) {
      console.error('Error during Google sign-in:', error.message);
    }
  };

  return (
    <div className="signup-container">
      <h2>Sign Up</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Password:</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="signup-button">Sign Up</button>
      </form>
      <button onClick={handleGoogleSignIn} className="google-signin-button">Sign up with Google</button>
    </div>
  );
};

export default Signup;
