import React, { useState } from 'react';
import './Navbar.css';

const Navbar = () => {
    const [dropdownVisible, setDropdownVisible] = useState(false);

    const toggleDropdown = () => {
        setDropdownVisible(!dropdownVisible);
    };

    return (
        <nav className="navbar">
            <h1>Perfume Shop</h1>
            <ul className="navbar-buttons">
                <li><a href="/" className="nav-button">Home</a></li>
                <li><a href="/collections" className="nav-button">Collections</a></li>
                <li><a href="/offers" className="nav-button">Offers</a></li>
                <li><a href="/about" className="nav-button">About</a></li>
                
                {/* Search Bar */}
                <li className="search-bar">
                    <input
                        type="text"
                        placeholder="Search for products, brands and more"
                        className="search-input"
                    />
                    <button className="search-button">üîç</button>
                </li>
                
                {/* Login Dropdown */}
                <li className="navbar-buttonsn">
                    <button onClick={toggleDropdown}>Login</button>
                    {dropdownVisible && (
                        <ul className="dropdown-menu">
                            <li><a href="/profile">My Profile</a></li>
                            <li><a href="/plus-zone">Flipkart Plus Zone</a></li>
                            <li><a href="/orders">Orders</a></li>
                            <li><a href="/wishlist">Wishlist</a></li>
                            <li><a href="/rewards">Rewards</a></li>
                            <li><a href="/gift-cards">Gift Cards</a></li>
                        </ul>
                    )}
                </li>
            </ul>
        </nav>
    );
};

export default Navbar;
