import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Update this import
import axios from 'axios'; // Import axios for making HTTP requests
import Navbar from '../components/Navbar';
import Banner from '../components/Banner';
import ProductCard from '../components/ProductCard';
import Footer from '../components/Footer';
import '../App.css';
import './HomePage.css';

const HomePage = () => {
  const navigate = useNavigate(); // Use useNavigate instead of useHistory
  const [products, setProducts] = useState([]); // State to hold products
  const [loading, setLoading] = useState(true); // State for loading status

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`); // Use navigate function for redirection
  };

  // Fetch products from the backend
  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/products'); // Adjust the URL as necessary
      setProducts(response.data); // Set the products from response
    } catch (error) {
      console.error('Error fetching products:', error);
    } finally {
      setLoading(false); // Set loading to false after fetching
    }
  };

  useEffect(() => {
    fetchProducts(); // Call fetchProducts on component mount
  }, []);

  if (loading) {
    return <div>Loading...</div>; // Show loading text while fetching
  }

  return (
    <div className="homepage">
      <Banner />
      <section className="products-section">
        <h2>Featured Perfumes</h2>
        <div className="products-grid">
          {products.map((product) => (
            <ProductCard 
              key={product._id} // Use _id from MongoDB for unique keys
              product={product} 
              onClick={() => handleProductClick(product._id)} // Pass _id for product click
            />
          ))}
        </div>
      </section>
      <Footer />
    </div>
  );
};

export default HomePage;
