// ProductCard.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import './ProductCard.css';
import 'font-awesome/css/font-awesome.min.css';

const ProductCard = ({ product }) => {
  const navigate = useNavigate();

  const viewDetails = () => {
    console.log(`Navigating to product with ID: ${product._id}`);
    navigate(`/product/${product._id}`); // Navigate to product page using MongoDB's _id
  };

  const handleButtonClick = (event) => {
    event.stopPropagation(); // Prevent the click from bubbling up to the product card
    // Add your button click logic here
    console.log("Button clicked");
  };

  return (
    <div className="product-card" onClick={viewDetails}>
      <div className="product-image-container">
        <img
          src={`http://localhost:5000${product.imageUrl}`} // Ensure this URL is correct
          alt={product.name}
          className="product-image"
          onError={(e) => { e.target.onerror = null; e.target.src = 'path/to/placeholder/image.jpg'; }} // Fallback image
        />
        <div className="action-icons">
          <button className="wishlist-icon" aria-label="Add to Wishlist" onClick={handleButtonClick}>
            <i className="fa fa-heart"></i>
          </button>
          <button className="search-icon" aria-label="View Details" onClick={handleButtonClick}>
            <i className="fa fa-search"></i>
          </button>
        </div>
      </div>
      <h3 className="product-name">{product.name}</h3>
      <p className="product-price">Rs. {product.price}</p>
      <button className="add-to-cart-btn" onClick={handleButtonClick}>Add to Cart</button>
    </div>
  );
};

export default ProductCard;
