import React from 'react';
import './ProductCard.css'; // Ensure you have a CSS file for styling

const ProductCard = ({ product }) => {
  return (
    <div className="product-card">
      <div className="product-image-container">
        <img src={product.imageUrl} alt={product.name} className="product-image" />
        <div className="action-icons">
          {/* Heart icon for Wishlist */}
          <button className="wishlist-icon" aria-label="Add to Wishlist">
            ‚ù§Ô∏è {/* You can replace with an actual icon from a library like FontAwesome */}
          </button>
          {/* Search (View details) icon */}
          <button className="search-icon" aria-label="View Details">
            üîç {/* Again, can be replaced with an actual icon */}
          </button>
          {/* Zoom icon */}
          <button className="zoom-icon" aria-label="Zoom">
            üîç
          </button>
        </div>
      </div>
      <h3 className="product-name">{product.name}</h3>
      <p className="product-price">Rs. {product.price}</p>
      <button className="add-to-cart-btn">Add to Cart</button>
    </div>
  );
};

export default ProductCard;
