// src/components/ProductPage.js

import React, { useEffect, useState } from 'react';
import ReviewForm from './ReviewForm';

const ProductPage = ({ productId }) => {
    const [reviews, setReviews] = useState([]);
    const [submissionMessage, setSubmissionMessage] = useState('');
    const [isError, setIsError] = useState(false);

    const fetchReviews = async () => {
        try {
            const response = await fetch(`http://localhost:5000/api/products/${productId}/reviews`);
            if (!response.ok) throw new Error('Failed to fetch reviews');
            const data = await response.json();
            setReviews(data); // Ensure this is an array
        } catch (error) {
            console.error('Error fetching reviews:', error);
            setIsError(true);
            setSubmissionMessage(error.message);
        }
    };

    useEffect(() => {
        fetchReviews();
    }, [productId]);

    const handleReviewSubmit = async (review) => {
        try {
            const response = await fetch(`http://localhost:5000/api/products/${productId}/reviews`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(review),
            });

            if (!response.ok) {
                const errorDetail = await response.text();
                throw new Error(`Failed to submit review: ${errorDetail}`);
            }

            // Fetch the updated reviews after submission
            fetchReviews();
            setSubmissionMessage('Review submitted successfully!');
            setIsError(false);
        } catch (error) {
            console.error('Error submitting review:', error);
            setSubmissionMessage(error.message);
            setIsError(true);
        }
    };

    return (
        <div>
            <h1>Product Details</h1>
            {/* Render product details here */}
            
            <h2>Reviews</h2>
            <ul>
                {reviews.map((review, index) => (
                    <li key={index}>
                        <strong>{review.username}</strong> - {review.rating} stars
                        <p>{review.comment}</p>
                    </li>
                ))}
            </ul>

            <ReviewForm
                onSubmit={handleReviewSubmit}
                submissionMessage={submissionMessage}
                isError={isError}
            />
        </div>
    );
};

export default ProductPage;
