import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import './ProductPage.css'; // Add your styles here

const productDetails = {
  1: {
    name: 'Air Shot Lotus',
    description: 'A refreshing floral fragrance that embodies the spirit of nature.',
    price: 49.99,
    sizes: ['50ml', '100ml', '150ml'],
    images: [
      '/product1_1.jpg',
      '/product1_2.jpg',
      '/product1_3.jpg',
    ],
    reviews: [
      { id: 1, username: 'John', comment: 'Absolutely love this scent!' },
      { id: 2, username: 'Jane', comment: 'It lasts all day, highly recommend!' },
    ],
  },
  2: {
    name: 'Air Shot Sweet',
    description: 'A warm and inviting scent perfect for any occasion.',
    price: 59.99,
    sizes: ['50ml', '100ml', '150ml'],
    images: [
      '/product2_1.jpg',
      '/product2_2.jpg',
      '/product2_3.jpg',
    ],
    reviews: [],
  },
  // Add other products here
};

const ProductPage = () => {
  const { productId } = useParams();
  const product = productDetails[productId];

  const [reviews, setReviews] = useState(product ? product.reviews : []);
  const [newReview, setNewReview] = useState('');

  const handleAddReview = () => {
    if (newReview) {
      const review = { id: reviews.length + 1, username: 'Guest', comment: newReview };
      setReviews([...reviews, review]);
      setNewReview('');
    }
  };

  const handleShare = () => {
    const url = window.location.href; // Get the current URL
    navigator.share({
      title: product.name,
      text: `Check out this product: ${product.name}`,
      url: url,
    });
  };

  return (
    <div className="product-page">
      {product ? (
        <>
          <h1>{product.name}</h1>
          <div className="product-details">
            <div className="product-gallery">
              {product.images.map((img, index) => (
                <img key={index} src={img} alt={`${product.name} ${index + 1}`} />
              ))}
            </div>
            <div className="product-info">
              <p className="description">{product.description}</p>
              <p className="price">${product.price.toFixed(2)}</p>
              <p>Available Sizes: {product.sizes.join(', ')}</p>
              <button>Add to Cart</button>
              <button onClick={handleShare}>Share</button>
            </div>
          </div>

          <div className="reviews-section">
            <h2>Reviews</h2>
            <div className="reviews">
              {reviews.length > 0 ? (
                reviews.map((review) => (
                  <div key={review.id} className="review">
                    <strong>{review.username}:</strong> {review.comment}
                  </div>
                ))
              ) : (
                <p>No reviews yet.</p>
              )}
            </div>

            <textarea
              value={newReview}
              onChange={(e) => setNewReview(e.target.value)}
              placeholder="Write a review..."
            />
            <button onClick={handleAddReview}>Submit Review</button>
          </div>
        </>
      ) : (
        <p>Product not found.</p>
      )}
    </div>
  );
};

export default ProductPage;
