import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './ProductPage.css';

const ProductPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({ rating: '', comment: '' });

  // Fetch product details
  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/products/${id}`);
        setProduct(response.data);
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    const fetchReviews = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/products/${id}/reviews`);
        setReviews(response.data);
      } catch (error) {
        console.error('Error fetching reviews:', error);
      }
    };

    fetchProduct();
    fetchReviews();
  }, [id]);

  // Handle new review submission
  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`http://localhost:5000/api/products/${id}/reviews`, newReview);
      setReviews([...reviews, response.data]);
      setNewReview({ rating: '', comment: '' });
    } catch (error) {
      console.error('Error submitting review:', error);
    }
  };

  return (
    <div className="product-page">
      {product && (
        <div className="product-details">
          <img src={`http://localhost:5000${product.imageUrl}`} alt={product.name} className="product-image" />
          <h2>{product.name}</h2>
          <p>Price: Rs. {product.price}</p>
          <p>{product.description}</p>
        </div>
      )}
      
      <div className="reviews-section">
        <h3>Customer Reviews</h3>
        {reviews.map((review) => (
          <div key={review._id} className="review">
            <p><strong>{review.user}</strong>: {review.comment}</p>
            <p>Rating: {review.rating} / 5</p>
          </div>
        ))}

        <form onSubmit={handleReviewSubmit} className="review-form">
          <h4>Write a Review</h4>
          <label>
            Rating:
            <input
              type="number"
              min="1"
              max="5"
              value={newReview.rating}
              onChange={(e) => setNewReview({ ...newReview, rating: e.target.value })}
              required
            />
          </label>
          <label>
            Comment:
            <textarea
              value={newReview.comment}
              onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
              required
            />
          </label>
          <button type="submit">Submit Review</button>
        </form>
      </div>
    </div>
  );
};

export default ProductPage;
