// HomePage.js

import React, { useState, useEffect } from 'react';
import './HomePage.css'; // For styling

const HomePage = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        // Fetch products from the backend
        fetch('/api/products')
            .then(response => response.json())
            .then(data => setProducts(data))
            .catch(error => console.error('Error:', error));
    }, []);

    return (
        <div className="homepage">
            {/* Navbar */}
            <nav className="navbar">
                <div className="logo">Perfume Shop</div>
                <ul className="nav-links">
                    <li><a href="#">Home</a></li>
                    <li><a href="#">Shop</a></li>
                    <li><a href="#">Men</a></li>
                    <li><a href="#">Women</a></li>
                    <li><a href="#">Unisex</a></li>
                    <li><a href="#">About Us</a></li>
                    <li><a href="#">Contact Us</a></li>
                </ul>
            </nav>

            {/* Banner */}
            <section className="banner">
                <h1>Luxurious Perfume</h1>
                <button className="cta-btn">View All</button>
            </section>

            {/* Product Cards */}
            <section className="products-section">
                <h2>Featured Perfumes</h2>
                <div className="product-grid">
                    {products.map((product) => (
                        <div className="product-card" key={product.id}>
                            <img src={product.imageUrl} alt={product.name} />
                            <h3>{product.name}</h3>
                            <p>{product.description}</p>
                            <p className="price">${product.price}</p>
                            <a href={`/product/${product.id}`} className="view-btn">View Details</a>
                        </div>
                    ))}
                </div>
            </section>
        </div>
    );
};

export default HomePage;
