import React, { useState, useEffect, useRef } from 'react';
import StarRatings from 'react-star-ratings';
import {
  FacebookShareButton,
  TwitterShareButton,
  WhatsappShareButton,
} from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp, FaShareAlt } from 'react-icons/fa';
import './ProductPage.css';

// Static data for product and additional products
const product = { /*...product details as defined above...*/ };
const additionalProducts = [ /*...additional products as defined above...*/ ];

const ProductPage = () => {
  const [showShareOptions, setShowShareOptions] = useState(false);
  const [showCollection, setShowCollection] = useState(false);
  const [reviews, setReviews] = useState(product.reviews);
  const [newReview, setNewReview] = useState({ username: '', rating: 1, comment: '' });
  
  const collectionRef = useRef(null);
  const shareUrl = window.location.href;

  // Toggle share options display
  const toggleShareOptions = () => {
    setShowShareOptions(!showShareOptions);
  };

  // Handle new review submission
  const handleReviewSubmit = (e) => {
    e.preventDefault();
    const updatedReviews = [...reviews, { ...newReview, id: reviews.length + 1 }];
    setReviews(updatedReviews); // Update the reviews state
    setNewReview({ username: '', rating: 1, comment: '' }); // Reset the form
  };

  // Observer for showing collection on scroll
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting) {
          setShowCollection(true);
        }
      },
      { threshold: 0.1 }
    );

    if (collectionRef.current) {
      observer.observe(collectionRef.current);
    }

    return () => {
      if (collectionRef.current) {
        observer.unobserve(collectionRef.current);
      }
    };
  }, []);

  return (
    <div className="product-page">
      {/* Product Info Section */}
      <div className="product-info">
        <img src={product.image} alt={product.name} className="product-image" />
        <div className="product-details">
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <div className="price-rating">
            <p className="product-price">${product.price.toFixed(2)}</p>
            <div className="product-rating">
              <StarRatings
                rating={product.rating}
                starRatedColor="gold"
                numberOfStars={5}
                name="rating"
                starDimension="20px"
                starSpacing="3px"
              />
              <span>{product.rating} / 5</span>
            </div>
          </div>
          <button className="add-to-cart-btn">Add to Cart</button>
          <button className="buy-now-btn">Buy Now</button>
          
          {/* Share Button */}
          <div className="share-container">
            <button className="share-btn" onClick={toggleShareOptions}>
              <FaShareAlt size={20} /> Share
            </button>
            {showShareOptions && (
              <div className="share-dropdown">
                <h4>Share via</h4>
                <FacebookShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaFacebook size={20} /> Facebook</button>
                </FacebookShareButton>
                <TwitterShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaTwitter size={20} /> Twitter</button>
                </TwitterShareButton>
                <WhatsappShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaWhatsapp size={20} /> WhatsApp</button>
                </WhatsappShareButton>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Product Variants Section */}
      <h2 className="product-variants-title">More Options</h2>
      <div className="product-variants">
        {product.variants.map((variant) => (
          <div key={variant.id} className="variant-card">
            <img src={variant.image} alt={variant.name} className="variant-image" />
            <div>
              <h3 className="variant-name">{variant.name}</h3>
              <p className="variant-price">${variant.price.toFixed(2)}</p>
            </div>
            <button className="view-variant-btn">View Product</button>
          </div>
        ))}
      </div>

      {/* Reviews Section */}
      <h2 className="review-title">Customer Reviews</h2>
      <div className="reviews-section">
        {reviews.map((review, index) => (
          <div key={index} className="review-card">
            <h4>{review.user}</h4>
            <StarRatings
              rating={review.rating}
              starRatedColor="gold"
              numberOfStars={5}
              name="rating"
              starDimension="18px"
              starSpacing="2px"
            />
            <p>{review.comment}</p>
          </div>
        ))}
      </div>
      <h3 className="add-review-title">Add a Review</h3>
      <form onSubmit={handleReviewSubmit} className="review-form">
        <input
          type="text"
          placeholder="Your Name"
          value={newReview.username}
          onChange={(e) => setNewReview({ ...newReview, username: e.target.value })}
          required
          className="review-input"
        />
        <select
          value={newReview.rating}
          onChange={(e) => setNewReview({ ...newReview, rating: Number(e.target.value) })}
          required
          className="review-input"
        >
          <option value="1">1 Star</option>
          <option value="2">2 Stars</option>
          <option value="3">3 Stars</option>
          <option value="4">4 Stars</option>
          <option value="5">5 Stars</option>
        </select>
        <textarea
          placeholder="Your Review"
          value={newReview.comment}
          onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
          required
          className="review-textarea"
        />
        <button type="submit" className="submit-review-btn">Submit Review</button>
      </form>

      {/* Collection Section */}
      <div ref={collectionRef} className="collection-section">
        <h2 className="more-products-title">More Products</h2>
        {showCollection && (
          <div className="more-products">
            {additionalProducts.map((product) => (
              <div key={product.id} className="more-product-card">
                <img src={product.image} alt={product.name} className="more-product-image" />
                <h3 className="more-product-name">{product.name}</h3>
                <p className="more-product-price">${product.price.toFixed(2)}</p>
                <button className="view-more-product-btn">View Product</button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ProductPage;
