// HomePage.js

import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import Banner from '../components/Banner';
import ProductCard from '../components/ProductCard';
import '../App.css';
import './HomePage.css'; // Import the new CSS file for styling

const HomePage = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('/api/products') // Fetch products from the API
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setProducts(data);
      })
      .catch(err => console.error('Error fetching products:', err));
  }, []);

  return (
    <div className="homepage">
      <Navbar />
      <Banner /> {/* Auto-changing banner */}
      <section className="products-section">
        <h2>Featured Perfumes</h2>
        <div className="products-grid"> {/* Container for product cards */}
          {products.length > 0 ? (
            products.map(product => (
              <ProductCard key={product._id} product={product} />
            ))
          ) : (
            <p>No products available.</p>
          )}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
