import React from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import './ProductCard.css';

const ProductCard = ({ product }) => {
  const navigate = useNavigate(); // Initialize navigate

  // Function to navigate to the product details page
  const viewDetails = () => {
    navigate(`/product/${product.id}`); // Navigate to product page with product ID
  };

  return (
    <div className="product-card">
      <div className="product-image-container">
        <img
          src={`/images/${product.imageUrl}`} // Correct path to the image in the public folder
          alt={product.name}
          className="product-image"
        />
        <div className="action-icons">
          {/* Heart icon for Wishlist */}
          <button className="wishlist-icon" aria-label="Add to Wishlist">
            ‚ù§Ô∏è
          </button>
          {/* Search (View details) icon */}
          <button className="search-icon" aria-label="View Details" onClick={viewDetails}>
            üîç
          </button>
          {/* Zoom icon */}
          <button className="zoom-icon" aria-label="Zoom">
            üîç
          </button>
        </div>
      </div>
      <h3 className="product-name">{product.name}</h3>
      <p className="product-price">Rs. {product.price}</p>
      <button className="add-to-cart-btn">Add to Cart</button>
    </div>
  );
};

export default ProductCard;
