import React, { useState, useEffect } from 'react';
import StarRatings from 'react-star-ratings';
import { FacebookShareButton, TwitterShareButton, WhatsappShareButton } from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp, FaShareAlt } from 'react-icons/fa';
import ReviewForm from '../components/ReviewForm';
import './ProductPage.css';

const ProductPage = () => {
    const [product, setProduct] = useState(null);
    const [showShareOptions, setShowShareOptions] = useState(false);
    const [reviews, setReviews] = useState([]);
    const [submissionMessage, setSubmissionMessage] = useState('');
    const [isError, setIsError] = useState(false);
    const [errorMessage, setErrorMessage] = useState('');
    const [additionalProducts, setAdditionalProducts] = useState([]);
    const [loading, setLoading] = useState(true);

    const productId = window.location.pathname.split('/').pop();
    const shareUrl = window.location.href;

    const toggleShareOptions = () => {
        setShowShareOptions(prev => !prev);
    };

    useEffect(() => {
        const fetchData = async () => {
            setLoading(true);
            try {
                const [productRes, reviewsRes, additionalRes] = await Promise.all([
                    fetch(`http://localhost:5000/api/products/${productId}`),
                    fetch(`http://localhost:5000/api/products/${productId}/reviews`),
                    fetch(`http://localhost:5000/api/products/recommended/${productId}`)
                ]);

                if (!productRes.ok || !reviewsRes.ok || !additionalRes.ok) {
                    throw new Error('Failed to fetch data');
                }

                const productData = await productRes.json();
                const reviewsData = await reviewsRes.json();
                const additionalData = await additionalRes.json();

                setProduct(productData);
                setReviews(reviewsData);
                setAdditionalProducts(additionalData);
            } catch (error) {
                setIsError(true);
                setErrorMessage(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, [productId]);

    const handleReviewSubmit = async (review) => {
        try {
            const response = await fetch(`http://localhost:5000/api/products/${productId}/reviews`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(review),
            });

            if (!response.ok) throw new Error('Failed to submit review');
            
            const result = await response.json();
            setSubmissionMessage(result.message);
            setIsError(false);
            setReviews(prev => [...prev, review]); // Optimistically update reviews
        } catch (error) {
            setSubmissionMessage('Error submitting review');
            setIsError(true);
        }
    };

    if (loading) return <div className="loading-spinner">Loading...</div>; // Add spinner

    if (!product) return <div>Error loading product. Please try again.</div>;

    return (
        <div className="product-page">
            {isError && <div className="error-message">{errorMessage}</div>}

            <div className="product-info">
                <img
                    src={product.image ? `http://localhost:5000${product.image}` : 'path/to/placeholder/image.jpg'}
                    alt={product.name}
                    className="product-image"
                />
                <div className="product-details">
                    <h1>{product.name}</h1>
                    <p>{product.description}</p>
                    <div className="price-rating">
                        <p className="product-price">${product.price.toFixed(2)}</p>
                        <div className="product-rating">
                            <StarRatings
                                rating={product.rating}
                                starRatedColor="gold"
                                numberOfStars={5}
                                name="rating"
                                starDimension="20px"
                                starSpacing="3px"
                            />
                            <span>{product.rating} / 5</span>
                        </div>
                    </div>
                    <button className="add-to-cart-btn" aria-label="Add to cart">Add to Cart</button>
                    <button className="buy-now-btn" aria-label="Buy now">Buy Now</button>

                    <div className="share-container">
                        <button className="share-btn" onClick={toggleShareOptions} aria-label="Share product">
                            <FaShareAlt size={20} /> Share
                        </button>
                        {showShareOptions && (
                            <div className="share-dropdown">
                                <h4>Share via</h4>
                                <FacebookShareButton url={shareUrl}>
                                    <button className="social-share-btn" aria-label="Share on Facebook"><FaFacebook size={20} /> Facebook</button>
                                </FacebookShareButton>
                                <TwitterShareButton url={shareUrl}>
                                    <button className="social-share-btn" aria-label="Share on Twitter"><FaTwitter size={20} /> Twitter</button>
                                </TwitterShareButton>
                                <WhatsappShareButton url={shareUrl}>
                                    <button className="social-share-btn" aria-label="Share on WhatsApp"><FaWhatsapp size={20} /> WhatsApp</button>
                                </WhatsappShareButton>
                            </div>
                        )}
                    </div>
                </div>
            </div>

            <h2 className="product-variants-title">More Options</h2>
            <div className="product-variants">
                {product.variants && product.variants.length > 0 ? (
                    product.variants.map((variant) => (
                        <div key={variant._id} className="variant-card">
                            <img
                                src={variant.image ? `http://localhost:5000${variant.image}` : 'path/to/placeholder/image.jpg'}
                                alt={variant.name}
                                className="variant-image"
                            />
                            <div>
                                <h3 className="variant-name">{variant.name}</h3>
                                <p className="variant-price">${variant.price.toFixed(2)}</p>
                            </div>
                            <button className="view-variant-btn" aria-label={`View ${variant.name}`}>View Product</button>
                        </div>
                    ))
                ) : (
                    <p>No additional variants available.</p>
                )}
            </div>

            <h2 className="review-title">Customer Reviews</h2>
            <div className="reviews-section">
                {reviews.length > 0 ? (
                    reviews.map((review, index) => (
                        <div key={index} className="review-card">
                            <h4>{review.username}</h4>
                            <StarRatings
                                rating={review.rating}
                                starRatedColor="gold"
                                numberOfStars={5}
                                name="userRating"
                                starDimension="15px"
                                starSpacing="2px"
                            />
                            <p>{review.comment}</p>
                        </div>
                    ))
                ) : (
                    <p>No reviews yet. Be the first to leave a review!</p>
                )}
            </div>

            <ReviewForm onSubmit={handleReviewSubmit} submissionMessage={submissionMessage} isError={isError} />

            <h2 className="recommended-products-title">You Might Also Like</h2>
            <div className="recommended-products">
                {additionalProducts.length > 0 ? (
                    additionalProducts.map((recommendedProduct) => (
                        <div key={recommendedProduct._id} className="recommended-product-card">
                            <img
                                src={recommendedProduct.image ? `http://localhost:5000${recommendedProduct.image}` : 'path/to/placeholder/image.jpg'}
                                alt={recommendedProduct.name}
                                className="recommended-product-image"
                            />
                            <h3>{recommendedProduct.name}</h3>
                            <p>${recommendedProduct.price.toFixed(2)}</p>
                            <button className="view-recommended-product-btn" aria-label={`View ${recommendedProduct.name}`}>View Product</button>
                        </div>
                    ))
                ) : (
                    <p>No recommended products available.</p>
                )}
            </div>
        </div>
    );
};

export default ProductPage;
