import React, { useState } from 'react';
import '../App.css';

const AccountPage = ({ user, onUpdateProfilePhoto }) => {
    const [selectedPhoto, setSelectedPhoto] = useState(user.profilePhoto || null);

    const handlePhotoChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onload = () => {
                setSelectedPhoto(reader.result);  // Display the uploaded photo immediately
                onUpdateProfilePhoto(reader.result);  // Trigger the function to save photo in backend or parent state
            };
            reader.readAsDataURL(file);
        }
    };

    return (
        <div>
            <h2>Account Details</h2>
            <div className="account-details">
                <div className="profile-photo-container">
                    {selectedPhoto ? (
                        <img src={selectedPhoto} alt="Profile" className="profile-photo-large" />
                    ) : (
                        <div className="profile-photo-placeholder">No Photo</div>
                    )}
                </div>
                <label className="upload-button">
                    Upload Photo
                    <input 
                        type="file" 
                        accept="image/*" 
                        onChange={handlePhotoChange} 
                        style={{ display: 'none' }} 
                    />
                </label>
                <h4>Name: {user.name}</h4>
                <h4>Email: {user.email}</h4>
            </div>

            <h3>Wallet</h3>
            <p>Balance: ${user.walletBalance}</p>

            <h3>Promo Codes</h3>
            <ul>
                {user.promoCodes.length > 0 ? (
                    user.promoCodes.map((code, index) => <li key={index}>{code}</li>)
                ) : (
                    <li>No promo codes available.</li>
                )}
            </ul>

            <h3>Purchase History</h3>
            <ul>
                {user.purchaseHistory.length > 0 ? (
                    user.purchaseHistory.map((purchase, index) => (
                        <li key={index}>
                            {purchase.productName} - ${purchase.amount}
                        </li>
                    ))
                ) : (
                    <li>No purchases made yet.</li>
                )}
            </ul>
        </div>
    );
};

export default AccountPage;
