import React, { useState } from 'react';
import '../App.css';

const Navbar = ({ user, onSignOut }) => {
    const [dropdownVisible, setDropdownVisible] = useState(false);

    const handleProfileClick = () => {
        setDropdownVisible(!dropdownVisible);
    };

    return (
        <nav className="navbar">
            <div className="navbar-logo">Perfume Shop</div>
            <ul className="navbar-links">
                <li>Home</li>
                <li>Collections</li>
                <li>About</li>
                <li>Contact</li>
            </ul>
            <div className="navbar-user">
                {user ? (
                    <div className="profile-dropdown">
                        <div className="profile-photo-container" onClick={handleProfileClick}>
                            <img
                                src={user.profilePhoto || '/images/default-avatar.png'}
                                alt="Profile"
                                className="profile-photo-small"
                            />
                        </div>

                        {dropdownVisible && (
                            <div className="dropdown-menu">
                                <div onClick={() => window.location.href = '/account'}>
                                    Account
                                </div>
                                <div onClick={onSignOut}>
                                    Sign Out
                                </div>
                            </div>
                        )}
                    </div>
                ) : (
                    <button className="login-button">Login</button>
                )}
            </div>
        </nav>
    );
};

export default Navbar;
