import React, { useState, useEffect } from 'react';
import StarRatings from 'react-star-ratings';
import ReviewForm from './ReviewForm';
import './ProductPage.css';

const ProductPage = () => {
  const [product, setProduct] = useState(null);
  const [reviews, setReviews] = useState([]);
  const productId = window.location.pathname.split('/').pop();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/products/${productId}`);
        if (!response.ok) throw new Error('Failed to fetch product');
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error('Failed to fetch product:', error);
      }
    };

    const fetchReviews = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/products/${productId}/reviews`);
        if (!response.ok) throw new Error('Failed to fetch reviews');
        const data = await response.json();
        setReviews(data);
      } catch (error) {
        console.error('Failed to fetch reviews:', error);
      }
    };

    fetchProduct();
    fetchReviews();
  }, [productId]);

  const handleReviewSubmitted = (newReview) => {
    setReviews((prevReviews) => [...prevReviews, newReview]);
  };

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div className="product-page">
      <div className="product-info">
        <img
          src={`/images/${product.image}`}
          alt={product.name}
          className="product-image"
        />
        <div className="product-details">
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <p className="product-price">
            Rs. {product.price ? product.price.toFixed(2) : '0.00'}
          </p>
          <StarRatings
            rating={product.rating || 0}
            starRatedColor="gold"
            numberOfStars={5}
            name="rating"
            starDimension="20px"
            starSpacing="3px"
          />
          <button className="add-to-cart-btn">Add to Cart</button>
          <button className="buy-now-btn">Buy Now</button>
        </div>
      </div>

      <h2 className="review-title">Customer Reviews</h2>
      <div className="reviews-section">
        {reviews.length > 0 ? (
          reviews.map((review) => (
            <div key={review._id} className="review-card">
              <h4>{review.userId?.name || 'Anonymous'}</h4>
              <StarRatings
                rating={review.rating}
                starRatedColor="gold"
                numberOfStars={5}
                name="rating"
                starDimension="15px"
                starSpacing="2px"
              />
              <p>{review.comment}</p>
            </div>
          ))
        ) : (
          <p>No reviews yet. Be the first to leave a review!</p>
        )}
      </div>

      <ReviewForm productId={product._id} onReviewSubmitted={handleReviewSubmitted} />
    </div>
  );
};

export default ProductPage;
