import React, { useState } from 'react';
import Navbar from './components/Navbar';
import Banner from './components/Banner';
import ProductCard from './components/ProductCard';
import ProductPage from './components/ProductPage';
import './App.css';

const mockUser = {
    name: 'John Doe',
    email: 'john.doe@example.com',
    profilePhoto: '/images/profile.jpg', // Replace with actual profile photo
    walletBalance: 150,
    promoCodes: ['SUMMER2023', 'FALLSALE'],
    purchaseHistory: [
        { productName: 'Soul EDP', amount: 999 },
        { productName: 'Ocean Breeze', amount: 799 },
    ]
};

const mockProducts = [
    { id: 1, name: 'Soul EDP', description: 'Long-lasting perfume', price: 999, image: '/images/soul.jpg', reviews: [], images: ['/images/soul1.jpg', '/images/soul2.jpg'] },
    // Add more products...
];

function App() {
    const [selectedProduct, setSelectedProduct] = useState(null);

    const handleProductClick = (id) => {
        const product = mockProducts.find((p) => p.id === id);
        setSelectedProduct(product);
    };

    const handleSignOut = () => {
        console.log('User signed out');
        // Clear user session logic
    };

    return (
        <div className="App">
            <Navbar user={mockUser} onSignOut={handleSignOut} />
            {selectedProduct ? (
                <ProductPage product={selectedProduct} />
            ) : (
                <>
                    <Banner />
                    <div className="product-grid">
                        {mockProducts.map((product) => (
                            <ProductCard key={product.id} product={product} onClick={handleProductClick} />
                        ))}
                    </div>
                </>
            )}
        </div>
    );
}

export default App;
