import React, { useState, useEffect, useRef } from 'react';
import StarRatings from 'react-star-ratings';
import {
  FacebookShareButton,
  TwitterShareButton,
  WhatsappShareButton,
} from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp, FaShareAlt } from 'react-icons/fa';
import './ProductPage.css';

const ProductPage = () => {
  const product = {
    id: 1,
    name: 'Elegant Perfume',
    description: 'Experience elegance in every spritz with this luxurious fragrance.',
    price: 49.99,
    rating: 4.5,
    image: '/images/elegant-perfume.jpg',
    variants: [
      {
        id: 1.1,
        name: 'Elegant Perfume - 50ml',
        price: 29.99,
        image: '/images/elegant-perfume-50ml.jpg',
      },
      {
        id: 1.2,
        name: 'Elegant Perfume - 100ml',
        price: 49.99,
        image: '/images/elegant-perfume-100ml.jpg',
      },
      {
        id: 1.3,
        name: 'Elegant Perfume - Limited Edition',
        price: 69.99,
        image: '/images/elegant-perfume-limited.jpg',
      },
    ],
  };

  const additionalProducts = [
    {
      id: 5,
      name: 'Fresh Citrus Perfume',
      price: 39.99,
      image: '/images/fresh-citrus-perfume.jpg',
    },
    {
      id: 6,
      name: 'Sweet Blossom Perfume',
      price: 45.99,
      image: '/images/sweet-blossom-perfume.jpg',
    },
    {
      id: 7,
      name: 'Classic Wood Perfume',
      price: 49.99,
      image: '/images/classic-wood-perfume.jpg',
    },
  ];

  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({ username: '', rating: 1, comment: '' });
  const [submissionMessage, setSubmissionMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const collectionRef = useRef(null);
  const [showShareOptions, setShowShareOptions] = useState(false); // Added state for share options
  const shareUrl = window.location.href;

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await fetch(`/api/products/${product.id}/reviews`);
        const data = await response.json();
        setReviews(data);
      } catch (error) {
        console.error('Error fetching reviews:', error);
      }
    };

    fetchReviews();
  }, []);

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`/api/products/${product.id}/reviews`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newReview),
      });

      if (!response.ok) {
        throw new Error('Failed to submit review');
      }

      const addedReview = await response.json();
      setReviews((prevReviews) => [...prevReviews, addedReview]);
      setNewReview({ username: '', rating: 1, comment: '' });
      setSubmissionMessage('Review submitted successfully!');
      setIsError(false);
    } catch (error) {
      console.error('Error submitting review:', error);
      setSubmissionMessage('Failed to submit review: Please try again.');
      setIsError(true);
    } finally {
      setTimeout(() => {
        setSubmissionMessage('');
      }, 3000);
    }
  };

  return (
    <div className="product-page">
      <div className="product-info">
        <img src={product.image} alt={product.name} className="product-image" />
        <div className="product-details">
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <div className="price-rating">
            <p className="product-price">${product.price.toFixed(2)}</p>
            <div className="product-rating">
              <StarRatings
                rating={product.rating}
                starRatedColor="gold"
                numberOfStars={5}
                name="rating"
                starDimension="20px"
                starSpacing="3px"
              />
              <span>{product.rating} / 5</span>
            </div>
          </div>
          <button className="add-to-cart-btn">Add to Cart</button>
          <button className="buy-now-btn">Buy Now</button>

          <div className="share-container">
            <button className="share-btn" onClick={() => setShowShareOptions((prev) => !prev)}>
              <FaShareAlt size={20} /> Share
            </button>
            {showShareOptions && (
              <div className="share-dropdown">
                <h4>Share via</h4>
                <FacebookShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaFacebook size={20} /> Facebook</button>
                </FacebookShareButton>
                <TwitterShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaTwitter size={20} /> Twitter</button>
                </TwitterShareButton>
                <WhatsappShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaWhatsapp size={20} /> WhatsApp</button>
                </WhatsappShareButton>
              </div>
            )}
          </div>
        </div>
      </div>

      <h2 className="review-title">Customer Reviews</h2>
      <div className="reviews-section">
        {reviews.length > 0 ? (
          reviews.map((review, index) => (
            <div key={index} className="review-card">
              <h4>{review.username}</h4>
              <StarRatings
                rating={review.rating}
                starRatedColor="gold"
                numberOfStars={5}
                name="rating"
                starDimension="18px"
                starSpacing="2px"
              />
              <p>{review.comment}</p>
            </div>
          ))
        ) : (
          <p>No reviews yet. Be the first to review this product!</p>
        )}
      </div>

      {/* Submission Message */}
      {submissionMessage && (
        <div className={`submission-message ${isError ? 'error' : 'success'}`}>
          {submissionMessage}
        </div>
      )}

      {/* Updated Review Form */}
      <h3 className="review-title">Add a Review</h3>
      <form onSubmit={handleReviewSubmit} className="review-form">
        <input
          type="text"
          placeholder="Your Name"
          value={newReview.username}
          onChange={(e) => setNewReview({ ...newReview, username: e.target.value })}
          required
          className="review-input"
        />

        <div className="star-rating-input">
          <label>Rating:</label>
          <StarRatings
            rating={newReview.rating}
            starRatedColor="gold"
            changeRating={(rating) => setNewReview({ ...newReview, rating })}
            numberOfStars={5}
            starDimension="20px"
            starSpacing="3px"
            name="rating"
          />
        </div>

        <textarea
          placeholder="Your Review"
          value={newReview.comment}
          onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
          required
          className="review-textarea"
        />
        <button type="submit" className="submit-review-btn">Submit Review</button>
      </form>

      {/* Collection Section */}
      <div ref={collectionRef}>
        <h2>Related Products</h2>
        <div className="additional-products">
          {additionalProducts.map((product) => (
            <div key={product.id} className="additional-product-card">
              <img src={product.image} alt={product.name} className="additional-product-image" />
              <h3>{product.name}</h3>
              <p>${product.price.toFixed(2)}</p>
              <button className="view-product-btn">View Product</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
