import React, { useState, useEffect, useRef } from 'react';
import StarRatings from 'react-star-ratings';
import {
  FacebookShareButton,
  TwitterShareButton,
  WhatsappShareButton,
} from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp, FaShareAlt } from 'react-icons/fa';
import './ProductPage.css';

const ProductPage = () => {
  const product = {
    id: 1,
    name: 'Elegant Perfume',
    description: 'Experience elegance in every spritz with this luxurious fragrance.',
    price: 49.99,
    rating: 4.5,
    image: '/images/elegant-perfume.jpg',
    variants: [
      {
        id: 1.1,
        name: 'Elegant Perfume - 50ml',
        price: 29.99,
        image: '/images/elegant-perfume-50ml.jpg',
      },
      {
        id: 1.2,
        name: 'Elegant Perfume - 100ml',
        price: 49.99,
        image: '/images/elegant-perfume-100ml.jpg',
      },
      {
        id: 1.3,
        name: 'Elegant Perfume - Limited Edition',
        price: 69.99,
        image: '/images/elegant-perfume-limited.jpg',
      },
    ],
    reviews: [
      { user: 'Alice', rating: 4, comment: 'Amazing scent!' },
      { user: 'Bob', rating: 5, comment: 'Best perfume I ever bought!' },
      { user: 'Claire', rating: 4.5, comment: 'Really luxurious and long-lasting.' },
    ],
  };

  const additionalProducts = [
    {
      id: 2,
      name: 'Fresh Citrus Perfume',
      price: 39.99,
      image: '/images/fresh-citrus-perfume.jpg',
    },
    {
      id: 3,
      name: 'Sweet Blossom Perfume',
      price: 45.99,
      image: '/images/sweet-blossom-perfume.jpg',
    },
    {
      id: 4,
      name: 'Classic Wood Perfume',
      price: 49.99,
      image: '/images/classic-wood-perfume.jpg',
    },
  ];

  const [showShareOptions, setShowShareOptions] = useState(false);
  const [newReview, setNewReview] = useState({ username: '', rating: 1, comment: '' });
  const collectionRef = useRef(null);

  const handleReviewSubmit = (e) => {
    e.preventDefault();
    product.reviews = [...product.reviews, { ...newReview, id: product.reviews.length + 1 }];
    setNewReview({ username: '', rating: 1, comment: '' });
  };

  const toggleShareOptions = () => {
    setShowShareOptions(!showShareOptions);
  };

  return (
    <div className="product-page">
      <div className="product-info">
        <img src={product.image} alt={product.name} className="product-image" />
        <div className="product-details">
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <p className="product-price">${product.price.toFixed(2)}</p>
          <StarRatings rating={product.rating} starRatedColor="gold" numberOfStars={5} />
          <button className="add-to-cart-btn">Add to Cart</button>
          <button className="buy-now-btn">Buy Now</button>
          
          <div className="share-container">
            <button className="share-btn" onClick={toggleShareOptions}>
              <FaShareAlt size={20} /> Share
            </button>
            {showShareOptions && (
              <div className="share-dropdown">
                <FacebookShareButton url={window.location.href}><FaFacebook size={20} /></FacebookShareButton>
                <TwitterShareButton url={window.location.href}><FaTwitter size={20} /></TwitterShareButton>
                <WhatsappShareButton url={window.location.href}><FaWhatsapp size={20} /></WhatsappShareButton>
              </div>
            )}
          </div>
        </div>
      </div>

      <h2>Available Variants</h2>
      <div className="product-variants">
        {product.variants.map((variant) => (
          <div key={variant.id} className="variant-card">
            <img src={variant.image} alt={variant.name} className="variant-image" />
            <h3>{variant.name}</h3>
            <p>${variant.price.toFixed(2)}</p>
            <button className="view-variant-btn">View Product</button>
          </div>
        ))}
      </div>

      <h2>Customer Reviews</h2>
      <div className="reviews-section">
        {product.reviews.map((review, index) => (
          <div key={index} className="review-card">
            <h4>{review.user}</h4>
            <StarRatings rating={review.rating} starRatedColor="gold" numberOfStars={5} />
            <p>{review.comment}</p>
          </div>
        ))}
      </div>

      <h3>Add a Review</h3>
      <form onSubmit={handleReviewSubmit} className="review-form">
        <input
          type="text"
          placeholder="Your Name"
          value={newReview.username}
          onChange={(e) => setNewReview({ ...newReview, username: e.target.value })}
          required
        />
        <StarRatings
          rating={newReview.rating}
          starRatedColor="gold"
          changeRating={(rating) => setNewReview({ ...newReview, rating })}
          numberOfStars={5}
        />
        <textarea
          placeholder="Your Review"
          value={newReview.comment}
          onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
          required
        />
        <button type="submit">Submit Review</button>
      </form>

      <div ref={collectionRef}>
        <h2>More Products</h2>
        <div className="additional-products">
          {additionalProducts.map((product) => (
            <div key={product.id} className="additional-product-card">
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <p>${product.price.toFixed(2)}</p>
              <button className="view-more-product-btn">View Product</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
