import React, { useState } from 'react';
import StarRatings from 'react-star-ratings';

const ReviewForm = ({ productId, onSubmit, submissionMessage, isError }) => {
    const [newReview, setNewReview] = useState({
        username: '',
        rating: 0,
        comment: '',
    });

    const handleReviewSubmit = async (e) => {
        e.preventDefault(); // Prevent default form submission

        try {
            const response = await fetch(`http://localhost:5000/api/products/${productId}/reviews`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(newReview), // Send the newReview object
            });

            if (!response.ok) {
                throw new Error(`Error: ${response.status} ${response.statusText}`);
            }

            // Call the onSubmit function passed down from the parent
            onSubmit(newReview); // Pass the new review to the parent component

            setNewReview({ username: '', rating: 0, comment: '' }); // Reset the form
        } catch (error) {
            console.error('Failed to submit review:', error);
            // Handle error appropriately
        }
    };

    return (
        <div>
            <form className="review-form" onSubmit={handleReviewSubmit}>
                <h2>Leave a Review</h2>
                <input
                    type="text"
                    placeholder="Your Name"
                    value={newReview.username}
                    onChange={(e) => setNewReview({ ...newReview, username: e.target.value })}
                    required
                />
                <StarRatings
                    rating={newReview.rating}
                    starRatedColor="gold"
                    numberOfStars={5}
                    name="rating"
                    starDimension="20px"
                    starSpacing="3px"
                    changeRating={(newRating) => setNewReview({ ...newReview, rating: newRating })}
                />
                <textarea
                    placeholder="Your Review"
                    value={newReview.comment}
                    onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
                    required
                />
                <button type="submit">Submit Review</button>
            </form>

            {submissionMessage && (
                <div className={isError ? 'error-message' : 'success-message'}>
                    {submissionMessage}
                </div>
            )}
        </div>
    );
};

export default ReviewForm;
