import React, { useState, useEffect, useRef } from 'react';
import StarRatings from 'react-star-ratings';
import {
  FacebookShareButton,
  TwitterShareButton,
  WhatsappShareButton,
} from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp, FaShareAlt } from 'react-icons/fa';
import { useHistory, useNavigate } from 'react-router-dom'; // Assuming you are using react-router-dom for navigation
import './ProductPage.css';

const product = {
  id: 1,
  name: 'Elegant Perfume',
  description: 'Experience elegance in every spritz with this luxurious fragrance.',
  price: 49.99,
  rating: 4.5,
  image: '/images/elegant-perfume.jpg',
  variants: [
    { id: 1.1, name: 'Elegant Perfume - 50ml', price: 29.99, image: '/images/elegant-perfume-50ml.jpg' },
    { id: 1.2, name: 'Elegant Perfume - 100ml', price: 49.99, image: '/images/elegant-perfume-100ml.jpg' },
    { id: 1.3, name: 'Elegant Perfume - Limited Edition', price: 69.99, image: '/images/elegant-perfume-limited.jpg' },
  ],
};

const additionalProducts = [
  { id: 5, name: 'Fresh Citrus Perfume', price: 39.99, image: '/images/fresh-citrus-perfume.jpg' },
  { id: 6, name: 'Sweet Blossom Perfume', price: 45.99, image: '/images/sweet-blossom-perfume.jpg' },
  { id: 7, name: 'Classic Wood Perfume', price: 49.99, image: '/images/classic-wood-perfume.jpg' },
];

const ProductPage = () => {
  const [showShareOptions, setShowShareOptions] = useState(false);
  const [showCollection, setShowCollection] = useState(false);
  const [reviews, setReviews] = useState([]);
  const collectionRef = useRef(null);
  const shareUrl = window.location.href;
  const productId = product.id;
  const history = useNavigate(); // for navigation

  useEffect(() => {
    const fetchReviews = async () => {
      try {
        const response = await fetch(`/api/products/${productId}/reviews`);
        if (!response.ok) {
          throw new Error('Failed to fetch reviews');
        }
        const data = await response.json();
        setReviews(data);
      } catch (error) {
        console.error('Error fetching reviews:', error);
      }
    };

    fetchReviews();
  }, [productId]);

  const toggleShareOptions = () => {
    setShowShareOptions(!showShareOptions);
  };

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting) {
          setShowCollection(true);
        }
      },
      { threshold: 0.1 }
    );

    if (collectionRef.current) {
      observer.observe(collectionRef.current);
    }

    return () => {
      if (collectionRef.current) {
        observer.unobserve(collectionRef.current);
      }
    };
  }, []);

  // Navigate to the review form page
  const handleAddReviewClick = () => {
    history.push(`/products/${productId}/review`);
  };

  return (
    <div className="product-page">
      <div className="product-info">
        <img src={product.image} alt={product.name} className="product-image" />
        <div className="product-details">
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <div className="price-rating">
            <p className="product-price">${product.price.toFixed(2)}</p>
            <div className="product-rating">
              <StarRatings
                rating={product.rating}
                starRatedColor="gold"
                numberOfStars={5}
                name="rating"
                starDimension="20px"
                starSpacing="3px"
              />
              <span>{product.rating} / 5</span>
            </div>
          </div>
          <button className="add-to-cart-btn">Add to Cart</button>
          <button className="buy-now-btn">Buy Now</button>

          <div className="share-container">
            <button className="share-btn" onClick={toggleShareOptions}>
              <FaShareAlt size={20} /> Share
            </button>
            {showShareOptions && (
              <div className="share-dropdown">
                <h4>Share via</h4>
                <FacebookShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaFacebook size={20} /> Facebook</button>
                </FacebookShareButton>
                <TwitterShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaTwitter size={20} /> Twitter</button>
                </TwitterShareButton>
                <WhatsappShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaWhatsapp size={20} /> WhatsApp</button>
                </WhatsappShareButton>
              </div>
            )}
          </div>
        </div>
      </div>

      <h2 className="product-variants-title">More Options</h2>
      <div className="product-variants">
        {product.variants.map((variant) => (
          <div key={variant.id} className="variant-card">
            <img src={variant.image} alt={variant.name} className="variant-image" />
            <div>
              <h3 className="variant-name">{variant.name}</h3>
              <p className="variant-price">${variant.price.toFixed(2)}</p>
            </div>
            <button className="view-variant-btn">View Product</button>
          </div>
        ))}
      </div>

      <h2 className="review-title">Customer Reviews</h2>
      <div className="reviews-section">
        {reviews.length > 0 ? (
          reviews.map((review) => (
            <div key={review.id} className="review-card">
              <h4>{review.username}</h4>
              <StarRatings
                rating={review.rating}
                starRatedColor="gold"
                numberOfStars={5}
                name="rating"
                starDimension="15px"
                starSpacing="2px"
              />
              <p>{review.comment}</p>
            </div>
          ))
        ) : (
          <p>No reviews yet. Be the first to leave a review!</p>
        )}
        <button className="add-review-btn" onClick={handleAddReviewClick}>
          Add Your Review
        </button>
      </div>

      <div ref={collectionRef} className={`additional-products ${showCollection ? 'show' : ''}`}>
        <h2>Recommended Products</h2>
        <div className="additional-products-grid">
          {additionalProducts.map((prod) => (
            <div key={prod.id} className="recommended-product">
              <img src={prod.image} alt={prod.name} className="recommended-product-image" />
              <h3>{prod.name}</h3>
              <p>${prod.price.toFixed(2)}</p>
              <button className="view-product-btn">View Product</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
