import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import StarRatingComponent from 'react-star-rating-component'; // Import the star rating component

const ProductPage = () => {
  const { id } = useParams();
  const [product, setProduct] = useState(null);
  const [recommendedProducts, setRecommendedProducts] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(0); // State for the star rating
  const [reviewText, setReviewText] = useState(''); // State for review text

  useEffect(() => {
    fetchProduct();
    fetchRecommendedProducts();
    fetchReviews();
  }, [id]);

  const fetchProduct = async () => {
    try {
      const response = await fetch(`/api/products/${id}`);
      if (!response.ok) throw new Error('Failed to fetch product');
      const data = await response.json();
      setProduct(data);
    } catch (error) {
      console.error('Error fetching product:', error);
    }
  };

  const fetchRecommendedProducts = async () => {
    try {
      const response = await fetch('/api/products/recommended');
      if (!response.ok) throw new Error('Failed to fetch recommended products');
      const data = await response.json();
      setRecommendedProducts(data);
    } catch (error) {
      console.error('Error fetching recommended products:', error);
    }
  };

  const fetchReviews = async () => {
    try {
      const response = await fetch(`/api/products/${id}/reviews`);
      if (!response.ok) throw new Error('Failed to fetch reviews');
      const data = await response.json();
      setReviews(data);
    } catch (error) {
      console.error('Error fetching reviews:', error);
    }
  };

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch(`/api/products/${id}/reviews`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ rating, reviewText }),
      });
      if (!response.ok) throw new Error('Failed to submit review');
      fetchReviews(); // Refresh reviews after submission
      setRating(0);
      setReviewText('');
    } catch (error) {
      console.error('Error submitting review:', error);
    }
  };

  if (!product) {
    return <div>Loading...</div>; // Loading state
  }

  return (
    <div className="product-page">
      <h1>{product.name}</h1>
      <img src={product.imageUrl} alt={product.name} /> {/* Ensure image URL is correct */}
      <p>{product.description}</p>
      <h2>Price: ${product.price}</h2>

      <h3>Reviews</h3>
      {reviews.map((review) => (
        <div key={review.id}>
          <StarRatingComponent 
            name={`rating-${review.id}`} 
            starCount={5} 
            value={review.rating} 
            editing={false} // Disable editing for existing reviews
          />
          <p>{review.text}</p>
        </div>
      ))}

      <h3>Add a Review</h3>
      <form onSubmit={handleReviewSubmit}>
        <StarRatingComponent 
          name="userRating" 
          starCount={5} 
          value={rating} 
          onStarClick={(nextValue) => setRating(nextValue)} // Handle rating selection
        />
        <textarea
          value={reviewText}
          onChange={(e) => setReviewText(e.target.value)}
          placeholder="Write your review here"
          required
        />
        <button type="submit">Submit Review</button>
      </form>

      <h3>You May Also Like</h3>
      <div className="recommended-products">
        {recommendedProducts.map((item) => (
          <div key={item.id}>
            <h4>{item.name}</h4>
            <img src={item.imageUrl} alt={item.name} />
            <p>Price: ${item.price}</p>
          </div>
        ))}
      </div>

      <style jsx>{`
        .product-page {
          padding: 20px;
        }
        .recommended-products {
          display: flex;
          flex-wrap: wrap;
          gap: 10px;
        }
        .recommended-products div {
          border: 1px solid #ccc;
          padding: 10px;
          width: 150px;
        }
      `}</style>
    </div>
  );
};

export default ProductPage;
