import React, { useState, useEffect } from 'react';
import StarRatings from 'react-star-ratings';

const ProductPage = () => {
    const [product, setProduct] = useState(null);
    const [reviews, setReviews] = useState([]);
    const [newReview, setNewReview] = useState({ username: '', rating: 1, comment: '' });
    const productId = window.location.pathname.split('/').pop(); // Extract product ID from URL

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/products/${productId}`);
                if (!response.ok) throw new Error('Failed to fetch product');
                const data = await response.json();
                setProduct(data);
            } catch (error) {
                console.error('Error fetching product:', error);
            }
        };

        const fetchReviews = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/products/${productId}/reviews`);
                if (!response.ok) throw new Error('Failed to fetch reviews');
                const data = await response.json();
                setReviews(data);
            } catch (error) {
                console.error('Error fetching reviews:', error);
            }
        };

        fetchProduct();
        fetchReviews();
    }, [productId]);

    const handleReviewSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch(`http://localhost:5000/api/products/${productId}/reviews`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(newReview),
            });
            if (!response.ok) throw new Error('Failed to submit review');
            const savedReview = await response.json();
            setReviews([...reviews, savedReview]); // Update reviews state with the new review
            setNewReview({ username: '', rating: 1, comment: '' }); // Reset form
        } catch (error) {
            console.error('Error submitting review:', error);
        }
    };

    if (!product) return <div>Loading...</div>;

    return (
        <div>
            <h1>{product.name}</h1>
            <img src={`http://localhost:5000${product.image}`} alt={product.name} />
            <p>{product.description}</p>
            <p>${product.price.toFixed(2)}</p>
            <StarRatings
                rating={product.rating}
                starRatedColor="gold"
                numberOfStars={5}
                name="rating"
                starDimension="20px"
                starSpacing="3px"
            />
            <h2>Customer Reviews</h2>
            <div>
                {reviews.map((review) => (
                    <div key={review._id}>
                        <h4>{review.username}</h4>
                        <StarRatings
                            rating={review.rating}
                            starRatedColor="gold"
                            numberOfStars={5}
                            name="userRating"
                            starDimension="15px"
                            starSpacing="2px"
                        />
                        <p>{review.comment}</p>
                    </div>
                ))}
            </div>
            <form onSubmit={handleReviewSubmit}>
                <input
                    type="text"
                    placeholder="Your Name"
                    value={newReview.username}
                    onChange={(e) => setNewReview({ ...newReview, username: e.target.value })}
                    required
                />
                <StarRatings
                    rating={newReview.rating}
                    starRatedColor="gold"
                    numberOfStars={5}
                    name="newRating"
                    changeRating={(newRating) => setNewReview({ ...newReview, rating: newRating })}
                />
                <textarea
                    placeholder="Your Review"
                    value={newReview.comment}
                    onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
                    required
                />
                <button type="submit">Submit Review</button>
            </form>
        </div>
    );
};

export default ProductPage;
