import React, { useEffect, useState } from 'react';
import Navbar from '../components/Navbar';
import Banner from '../components/Banner';
import ProductCard from '../components/ProductCard';
import '../App.css';

const HomePage = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetch('/api/products')
      .then(response => response.json())
      .then(data => {
        console.log(data); // Log the fetched data
        setProducts(data);
      })
      .catch(err => console.error('Error fetching products:', err));
  }, []);

  return (
    <div className="homepage">
      <Navbar />
      <Banner /> {/* Auto-changing banner */}
      <section className="products-section">
        <h2>Featured Perfumes</h2>
        <div className="products-grid"> {/* Container for product cards */}
          {products.length > 0 ? (
            products.map(product => (
              <ProductCard key={product._id} product={product} /> // Render ProductCard for each product
            ))
          ) : (
            <p>No products available.</p> // Message when no products are found
          )}
        </div>
      </section>
    </div>
  );
};

export default HomePage;
