import React from 'react';
import '../App.css';

const ProductCard = ({ product, onClick }) => (
    <div className="product-card" onClick={() => onClick(product.id)}>
        <img src={product.image} alt={product.name} />
        <h3>{product.name}</h3>
        <p>{product.description}</p>
        <p>${product.price}</p>
    </div>
);

export default ProductCard;
