// src/pages/HomePage.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import Navbar from '../components/Navbar';
import Banner from '../components/Banner';
import ProductCard from '../components/ProductCard';
import Footer from '../components/Footer'; // Ensure correct casing
import '../App.css';
import './HomePage.css';

const HomePage = () => {
  const navigate = useNavigate();

  const products = [
    {
      id: 1,
      name: 'Elegant Perfume',
      price: 49.99, // Consistent data type
      imageUrl: '/elegant-perfume.jpg'
    },
    {
      id: 2,
      name: 'Air Shot Lotus',
      price: 199,
      imageUrl: '/air-shot-lotus.jpg'
    },
    // Add the rest of your products here...
  ];

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  return (
    <div className="homepage">
      <Banner />
      <section className="products-section">
        <h2>Featured Perfumes</h2>
        <div className="products-grid">
          {products.map((product) => (
            <ProductCard key={product.id} product={product} onClick={() => handleProductClick(product.id)} />
          ))}
        </div>
      </section>
      <Footer />
    </div>
  );
};

export default HomePage;
