// src/pages/ProductPage.js
import React from 'react';
import { useParams } from 'react-router-dom';

const ProductPage = () => {
  const { productId } = useParams();

  // Fetch product details based on productId or use a hardcoded example
  const productDetails = {
    1: { name: 'Air Shot Lotus', ingredients: 'Example ingredients', features: 'Example features' },
    2: { name: 'Air Shot Sweet', ingredients: 'Example ingredients', features: 'Example features' },
    // Add other products here
  };

  const product = productDetails[productId];

  return (
    <div className="product-page">
      <h2>{product.name}</h2>
      <img src={`/images/${product.imageUrl}`} alt={product.name} className="product-detail-image" />
      <p className="product-price">Price: Rs. {product.price}</p>
      <div className="product-ingredients">
        <h3>Ingredients:</h3>
        <ul>
          {product.ingredients.map((ingredient, index) => (
            <li key={index}>{ingredient}</li>
          ))}
        </ul>
      </div>
      <div className="product-features">
        <h3>Features:</h3>
        <ul>
          {product.features.map((feature, index) => (
            <li key={index}>{feature}</li>
          ))}
        </ul>
      </div>
      <div className="product-actions">
        <button className="buy-btn">Buy Now</button>
        <button className="add-to-cart-btn">Add to Cart</button>
      </div>
    </div>
  );
};

export default ProductPage;
