// src/pages/ProductPage.js
import React from 'react';
import { useParams } from 'react-router-dom';
import './ProductPage.css'; // Assuming you have a CSS file for styling

const ProductPage = () => {
  const { productId } = useParams();

  // Mock product data
  const productDetails = {
    1: {
      name: 'Air Shot Lotus',
      imageUrl: '/airshot-lotus.jpg',
      ingredients: 'Lotus Extract, Water, Aloe Vera',
      features: 'Hydrating, Refreshing, Lightweight',
      description: 'A lightweight, refreshing air mist that revitalizes your skin.',
      reviews: [
        { user: 'Jane Doe', comment: 'Amazing product, so refreshing!' },
        { user: 'John Smith', comment: 'Leaves my skin feeling great.' },
      ],
    },
    2: {
      name: 'Air Shot Sweet',
      imageUrl: '/airshot-sweet.jpg',
      ingredients: 'Sweet Berry Extract, Vitamin E',
      features: 'Nourishing, Sweet Fragrance',
      description: 'A nourishing spray with a sweet fragrance that lingers all day.',
      reviews: [
        { user: 'Alice Brown', comment: 'Love the smell, it’s so sweet!' },
      ],
    },
    // Add more products as needed
  };

  const product = productDetails[productId];

  return (
    <div className="product-page">
      {product ? (
        <>
          <div className="product-info">
            <img src={product.imageUrl} alt={product.name} className="product-image" />
            <div className="product-details">
              <h1>{product.name}</h1>
              <p className="product-description">{product.description}</p>
              <p><strong>Ingredients:</strong> {product.ingredients}</p>
              <p><strong>Features:</strong> {product.features}</p>
              <button className="add-to-cart-btn">Add to Cart</button>
              <button className="buy-now-btn">Buy Now</button>
            </div>
          </div>
          <div className="product-reviews">
            <h2>Customer Reviews</h2>
            {product.reviews.length > 0 ? (
              product.reviews.map((review, index) => (
                <div key={index} className="review">
                  <p><strong>{review.user}</strong></p>
                  <p>{review.comment}</p>
                </div>
              ))
            ) : (
              <p>No reviews yet.</p>
            )}
          </div>
        </>
      ) : (
        <p>Product not found.</p>
      )}
    </div>
  );
};

export default ProductPage;
