import React, { useState, useEffect, useRef } from 'react';
import StarRatings from 'react-star-ratings';
import {
  FacebookShareButton,
  TwitterShareButton,
  WhatsappShareButton,
} from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp, FaShareAlt } from 'react-icons/fa';
import './ProductPage.css';

const ProductPage = () => {
  const [product, setProduct] = useState(null); // Initialize state for product
  const [showShareOptions, setShowShareOptions] = useState(false);
  const [showCollection, setShowCollection] = useState(false);
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({ username: '', rating: 1, comment: '' });
  const [submissionMessage, setSubmissionMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const collectionRef = useRef(null);
  const shareUrl = window.location.href;
  const productId = 1; // Assuming this comes from the URL or props

  useEffect(() => {
    // Fetch product data
    const fetchProduct = async () => {
      try {
        const response = await fetch(`/api/products/${productId}`);
        if (!response.ok) throw new Error('Failed to fetch product');
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    const fetchReviews = async () => {
      try {
        const response = await fetch(`/api/products/${productId}/reviews`);
        if (!response.ok) throw new Error('Failed to fetch reviews');
        const data = await response.json();
        setReviews(data);
      } catch (error) {
        console.error('Error fetching reviews:', error);
      }
    };

    fetchProduct();
    fetchReviews();
  }, [productId]);

  const handleReviewSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`/api/products/${productId}/reviews`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newReview),
      });

      if (!response.ok) {
        throw new Error('Failed to submit review');
      }

      const addedReview = await response.json();
      setReviews((prevReviews) => [...prevReviews, addedReview]);
      setNewReview({ username: '', rating: 1, comment: '' });
      setSubmissionMessage('Review submitted successfully!');
      setIsError(false);
    } catch (error) {
      console.error('Error submitting review:', error);
      setSubmissionMessage('Failed to submit review: Please try again.');
      setIsError(true);
    } finally {
      setTimeout(() => {
        setSubmissionMessage('');
      }, 3000);
    }
  };

  // ... (Rest of the component remains the same)

  if (!product) return <div>Loading...</div>; // Handle loading state

  return (
    <div className="product-page">
      <div className="product-info">
        <img src={`http://localhost:5000${product.image}`} alt={product.name} className="product-image" />
        <div className="product-details">
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <div className="price-rating">
            <p className="product-price">${product.price.toFixed(2)}</p>
            <div className="product-rating">
              <StarRatings
                rating={product.rating}
                starRatedColor="gold"
                numberOfStars={5}
                name="rating"
                starDimension="20px"
                starSpacing="3px"
              />
              <span>{product.rating} / 5</span>
            </div>
          </div>
          <button className="add-to-cart-btn">Add to Cart</button>
          <button className="buy-now-btn">Buy Now</button>

          <div className="share-container">
            <button className="share-btn" onClick={toggleShareOptions}>
              <FaShareAlt size={20} /> Share
            </button>
            {showShareOptions && (
              <div className="share-dropdown">
                <h4>Share via</h4>
                <FacebookShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaFacebook size={20} /> Facebook</button>
                </FacebookShareButton>
                <TwitterShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaTwitter size={20} /> Twitter</button>
                </TwitterShareButton>
                <WhatsappShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaWhatsapp size={20} /> WhatsApp</button>
                </WhatsappShareButton>
              </div>
            )}
          </div>
        </div>
      </div>

      <h2 className="product-variants-title">More Options</h2>
      <div className="product-variants">
        {product.variants.map((variant) => (
          <div key={variant.id} className="variant-card">
            <img src={variant.image} alt={variant.name} className="variant-image" />
            <div>
              <h3 className="variant-name">{variant.name}</h3>
              <p className="variant-price">${variant.price.toFixed(2)}</p>
            </div>
            <button className="view-variant-btn">View Product</button>
          </div>
        ))}
      </div>

      <h2 className="review-title">Customer Reviews</h2>
      <div className="reviews-section">
        {reviews.length > 0 ? (
          reviews.map((review) => (
            <div key={review.id} className="review-card">
              <h4>{review.username}</h4>
              <StarRatings
                rating={review.rating}
                starRatedColor="gold"
                numberOfStars={5}
                name="rating"
                starDimension="15px"
                starSpacing="2px"
              />
              <p>{review.comment}</p>
            </div>
          ))
        ) : (
          <p>No reviews yet. Be the first to leave a review!</p>
        )}
      </div>

      <form className="review-form" onSubmit={handleReviewSubmit}>
        <h2>Leave a Review</h2>
        <input
          type="text"
          placeholder="Your Name"
          value={newReview.username}
          onChange={(e) => setNewReview({ ...newReview, username: e.target.value })}
          required
        />
        <StarRatings
          rating={newReview.rating}
          starRatedColor="gold"
          numberOfStars={5}
          name="rating"
          starDimension="20px"
          starSpacing="3px"
          changeRating={(newRating) => setNewReview({ ...newReview, rating: newRating })}
        />
        <textarea
          placeholder="Your Review"
          value={newReview.comment}
          onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
          required
        />
        <button type="submit">Submit Review</button>
      </form>

      {submissionMessage && (
        <div className={isError ? 'error-message' : 'success-message'}>
          {submissionMessage}
        </div>
      )}

      <div ref={collectionRef} className={`additional-products ${showCollection ? 'show' : ''}`}>
        <h2>Recommended Products</h2>
        <div className="additional-products-grid">
          {additionalProducts.map((prod) => (
            <div key={prod.id} className="recommended-product">
              <img src={prod.image} alt={prod.name} className="recommended-product-image" />
              <h3>{prod.name}</h3>
              <p>${prod.price.toFixed(2)}</p>
              <button className="view-product-btn">View Product</button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ProductPage;
