// src/pages/ProductPage.js
import React from 'react';
import { useParams } from 'react-router-dom';

const ProductPage = () => {
  const { productId } = useParams();

  // Fetch product details based on productId or use a hardcoded example
  const productDetails = {
    1: { name: 'Air Shot Lotus', ingredients: 'Example ingredients', features: 'Example features' },
    2: { name: 'Air Shot Sweet', ingredients: 'Example ingredients', features: 'Example features' },
    // Add other products here
  };

  const product = productDetails[productId];

  return (
    <div className="product-page">
      {product ? (
        <>
          <h1>{product.name}</h1>
          <p>Ingredients: {product.ingredients}</p>
          <p>Features: {product.features}</p>
          <button>Add to Cart</button>
          <button>Buy Now</button>
        </>
      ) : (
        <p>Product not found.</p>
      )}
    </div>
  );
};

export default ProductPage;
