import React, { useState, useEffect } from 'react';
import axios from 'axios';
import StarRatings from 'react-star-ratings';
import { FacebookShareButton, TwitterShareButton, WhatsappShareButton } from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp, FaShareAlt } from 'react-icons/fa';
import './ProductPage.css';

const ProductPage = ({ productId }) => {
  const [product, setProduct] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({ username: '', rating: 1, comment: '' });
  const [showShareOptions, setShowShareOptions] = useState(false);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(`/api/products/${productId}`);
        setProduct(response.data);
        setReviews(response.data.reviews || []);
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };
    fetchProduct();
  }, [productId]);

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`/api/products/${productId}/reviews`, newReview);
      setReviews([...reviews, response.data]);
      setNewReview({ username: '', rating: 1, comment: '' });
    } catch (error) {
      console.error('Error submitting review:', error);
    }
  };

  return (
    <div className="product-page">
      {product ? (
        <>
          <div className="product-info">
            <img src={product.imageUrl} alt={product.name} className="product-image" />
            <h1>{product.name}</h1>
            <p>{product.description}</p>
            <div className="price-rating">
              <p className="product-price">${product.price.toFixed(2)}</p>
              <div className="product-rating">
                <StarRatings
                  rating={product.rating}
                  starRatedColor="gold"
                  numberOfStars={5}
                  starDimension="20px"
                  starSpacing="3px"
                />
                <span>{product.rating} / 5</span>
              </div>
            </div>
          </div>

          <div className="review-section">
            <h2>Customer Reviews</h2>
            {reviews.length > 0 ? (
              reviews.map((review) => (
                <div key={review._id} className="review-card">
                  <h4>{review.username}</h4>
                  <StarRatings
                    rating={review.rating}
                    starRatedColor="gold"
                    numberOfStars={5}
                    starDimension="15px"
                    starSpacing="3px"
                  />
                  <p>{review.comment}</p>
                </div>
              ))
            ) : (
              <p>No reviews yet.</p>
            )}

            <form onSubmit={handleReviewSubmit} className="review-form">
              <input
                type="text"
                placeholder="Your Name"
                value={newReview.username}
                onChange={(e) => setNewReview({ ...newReview, username: e.target.value })}
                required
              />
              <StarRatings
                rating={newReview.rating}
                starRatedColor="gold"
                numberOfStars={5}
                name="rating"
                changeRating={(newRating) => setNewReview({ ...newReview, rating: newRating })}
              />
              <textarea
                placeholder="Your Review"
                value={newReview.comment}
                onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
                required
              />
              <button type="submit">Submit Review</button>
            </form>
          </div>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ProductPage;
