// src/App.js
import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar'; // Optional: Add a Navbar
import HomePage from './pages/HomePage'; // Ensure this uses default export
import ProductPage from './components/ProductPage'; // Ensure this uses default export
import Collections from './components/Collections'; // Ensure this uses default export
import About from './components/About'; // Ensure this uses default export
import Offers from './components/Offers'; // Ensure this uses default export
import Signup from './components/Signup'; // Ensure this uses default export

const App = () => {
  return (
    <Router>
      <Navbar /> {/* Add Navbar component if created */}
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/product/:id" element={<ProductPage />} /> {/* Dynamic route */}
        <Route path="/collections" element={<Collections />} />
        <Route path="/about" element={<About />} />
        <Route path="/offers" element={<Offers />} />
        <Route path="/signup" element={<Signup />} />
      </Routes>
    </Router>
  );
};

export default App;
