// src/pages/ProductPage.js
import React, { useState, useEffect } from 'react';
import { FacebookShareButton, TwitterShareButton, WhatsappShareButton } from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp, FaShareAlt } from 'react-icons/fa';
import './ProductPage.css';

const ProductPage = () => {
  const [product, setProduct] = useState(null); // State for the product
  const [showShareOptions, setShowShareOptions] = useState(false); // State for toggling share options
  const [reviews, setReviews] = useState([]); // State for reviews
  const [newReview, setNewReview] = useState({ username: '', rating: 1, comment: '' }); // State for new review input
  const [submissionMessage, setSubmissionMessage] = useState(''); // State for submission messages
  const [isError, setIsError] = useState(false); // State to track error messages
  const [additionalProducts, setAdditionalProducts] = useState([]); // State for recommended products

  const productId = window.location.pathname.split('/').pop(); // Get product ID from URL

  const shareUrl = window.location.href; // URL to be shared

  const toggleShareOptions = () => {
    setShowShareOptions((prev) => !prev);
  };

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/products/${productId}`);
        if (!response.ok) {
          // Check for specific error status codes
          if (response.status === 404) {
            throw new Error('Product not found');
          } else if (response.status === 500) {
            throw new Error('Server error, please try again later');
          } else {
            throw new Error('An error occurred while fetching product data');
          }
        }
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error('Error fetching product:', error);
        setSubmissionMessage(error.message); // Display specific error message to the user
        setIsError(true);
      }
    };

    const fetchReviews = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/products/${productId}/reviews`);
        if (!response.ok) {
          throw new Error('Failed to fetch reviews');
        }
        const data = await response.json();
        setReviews(data);
      } catch (error) {
        console.error('Error fetching reviews:', error);
        setSubmissionMessage('Failed to load reviews. Please try again later.');
        setIsError(true);
      }
    };

    const fetchAdditionalProducts = async () => {
      try {
        const response = await fetch(`http://localhost:5000/api/products/recommended`);
        if (!response.ok) {
          throw new Error('Failed to fetch additional products');
        }
        const data = await response.json();
        setAdditionalProducts(data);
      } catch (error) {
        console.error('Error fetching additional products:', error);
        setSubmissionMessage('Failed to load recommended products. Please try again later.');
        setIsError(true);
      }
    };

    fetchProduct();
    fetchReviews();
    fetchAdditionalProducts();
  }, [productId]);

  const handleReviewSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`http://localhost:5000/api/products/${productId}/reviews`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newReview),
      });

      if (!response.ok) {
        throw new Error('Failed to submit review');
      }

      const addedReview = await response.json();
      setReviews((prevReviews) => [...prevReviews, addedReview]);
      setNewReview({ username: '', rating: 1, comment: '' });
      setSubmissionMessage('Review submitted successfully!');
      setIsError(false);
    } catch (error) {
      console.error('Error submitting review:', error);
      setSubmissionMessage('Failed to submit review: Please try again.');
      setIsError(true);
    } finally {
      setTimeout(() => {
        setSubmissionMessage('');
      }, 3000);
    }
  };

  const handleStarClick = (rating) => {
    setNewReview((prev) => ({ ...prev, rating })); // Set the selected star rating
  };

  if (!product) return <div>Loading...</div>; // Show loading state if product is not fetched yet

  return (
    <div className="product-page">
      <div className="product-info">
        <img src={`http://localhost:5000${product.image}`} alt={product.name} className="product-image" />
        <div className="product-details">
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <div className="price-rating">
            <p className="product-price">${product.price.toFixed(2)}</p>
          </div>
          <button className="add-to-cart-btn">Add to Cart</button>
          <button className="buy-now-btn">Buy Now</button>

          <div className="share-container">
            <button className="share-btn" onClick={toggleShareOptions}>
              <FaShareAlt size={20} /> Share
            </button>
            {showShareOptions && (
              <div className="share-dropdown">
                <h4>Share via</h4>
                <FacebookShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaFacebook size={20} /> Facebook</button>
                </FacebookShareButton>
                <TwitterShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaTwitter size={20} /> Twitter</button>
                </TwitterShareButton>
                <WhatsappShareButton url={shareUrl}>
                  <button className="social-share-btn"><FaWhatsapp size={20} /> WhatsApp</button>
                </WhatsappShareButton>
              </div>
            )}
          </div>
        </div>
      </div>

      <h2 className="review-title">Customer Reviews</h2>
      <div className="reviews-section">
        {reviews.length > 0 ? (
          reviews.map((review) => (
            <div key={review.id} className="review-card">
              <h4>{review.username}</h4>
              <p>Rating: {review.rating} / 5</p>
              <p>{review.comment}</p>
            </div>
          ))
        ) : (
          <p>No reviews yet. Be the first to leave a review!</p>
        )}
      </div>

      <form className="review-form" onSubmit={handleReviewSubmit}>
        <h2>Leave a Review</h2>
        <input
          type="text"
          placeholder="Your Name"
          value={newReview.username}
          onChange={(e) => setNewReview({ ...newReview, username: e.target.value })}
          required
        />
        
        {/* Star Rating Selection */}
        <div className="star-rating-selection">
          <span>Select Rating: </span>
          {[1, 2, 3, 4, 5].map((star) => (
            <button
              key={star}
              type="button"
              className={`star-button ${newReview.rating === star ? 'selected' : ''}`}
              onClick={() => handleStarClick(star)}
            >
              {star} â˜…
            </button>
          ))}
        </div>

        <textarea
          placeholder="Your Review"
          value={newReview.comment}
          onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
          required
        />
        <button type="submit">Submit Review</button>
      </form>

      {submissionMessage && (
        <div className={`submission-message ${isError ? 'error' : ''}`}>
          {submissionMessage}
        </div>
      )}

      <h2 className="recommended-products-title">You Might Also Like</h2>
      <div className="recommended-products">
        {additionalProducts.map((recommendedProduct) => (
          <div key={recommendedProduct._id} className="recommended-product-card">
            <img src={`http://localhost:5000${recommendedProduct.image}`} alt={recommendedProduct.name} className="recommended-product-image" />
            <h3>{recommendedProduct.name}</h3>
            <p>${recommendedProduct.price.toFixed(2)}</p>
            <button className="view-recommended-btn">View Product</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductPage;
