// src/pages/ProductPage.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ProductPage = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState({});
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({
    userName: '',
    rating: 1,
    comment: '',
    imageUrl: '',
  });

  useEffect(() => {
    // Fetch product details
    const fetchProduct = async () => {
      const response = await axios.get(`/api/products/${productId}`);
      setProduct(response.data);
    };

    // Fetch reviews for the product
    const fetchReviews = async () => {
      const response = await axios.get(`/api/reviews/${productId}`);
      setReviews(response.data);
    };

    fetchProduct();
    fetchReviews();
  }, [productId]);

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    await axios.post('/api/reviews', { productId, ...newReview });
    setNewReview({ userName: '', rating: 1, comment: '', imageUrl: '' });
    // Fetch reviews again to get the updated list
    const response = await axios.get(`/api/reviews/${productId}`);
    setReviews(response.data);
  };

  return (
    <div>
      <h1>{product.name}</h1>
      <img src={`/images/${product.imageUrl}`} alt={product.name} />
      <p>Price: Rs. {product.price}</p>
      <p>{product.description}</p>

      <h2>Reviews</h2>
      <form onSubmit={handleReviewSubmit}>
        <input
          type="text"
          placeholder="Your Name"
          value={newReview.userName}
          onChange={(e) => setNewReview({ ...newReview, userName: e.target.value })}
          required
        />
        <select
          value={newReview.rating}
          onChange={(e) => setNewReview({ ...newReview, rating: e.target.value })}
        >
          {[1, 2, 3, 4, 5].map((star) => (
            <option key={star} value={star}>{star} Star</option>
          ))}
        </select>
        <textarea
          placeholder="Your Review"
          value={newReview.comment}
          onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
          required
        />
        <button type="submit">Submit Review</button>
      </form>

      <div>
        {reviews.map((review) => (
          <div key={review._id}>
            <h3>{review.userName}</h3>
            <p>Rating: {review.rating} Stars</p>
            <p>{review.comment}</p>
            {review.imageUrl && <img src={`/images/${review.imageUrl}`} alt="Review" />}
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductPage;
