// src/pages/HomePage.js
import React from 'react';
import { useNavigate } from 'react-router-dom'; // Update this import
import Navbar from '../components/Navbar';
import Banner from '../components/Banner';
import ProductCard from '../components/ProductCard';
import Footer from '../components/footer';
import '../App.css';
import './HomePage.css';
import '../components/About';

const HomePage = () => {
  const navigate = useNavigate(); // Use useNavigate instead of useHistory

  const products = [
    {
     id: 1,
     name: 'Elegant Perfume',
     price: "49.99",
     imageUrl: '/images/elegant-perfume.jpg' // Matches the file name in the images folder
   },
   {
     id: 2,
     name: 'Air Shot Lotus',
     price: 199,
     imageUrl: '/images/air-shot-lotus.jpg'
   },
   {
     id: 3,
     name: 'Air Shot Romance',
     price: 199,
     imageUrl: '/images/air-shot-romance.jpg'
   },
   {
     id: 4,
     name: 'Air Shot Oud',
     price: 199,
     imageUrl: '/images/air-shot-oud.jpg'
   },  {
     id: 5,
     name: 'Air Shot Lotus',
     price: 169,
     imageUrl: '/imagess/air-shot-lotus.jpg' // Matches the file name in the images folder
   },
   {
     id: 6,
     name: 'Air Shot Sweet',
     price: 199,
     imageUrl: '/images/air-shot-sweet.jpg'
   },
   {
     id: 7,
     name: 'Air Shot Romance',
     price: 199,
     imageUrl: '/images/air-shot-romance.jpg'
   },
   {
     id:8,
     name: 'Air Shot Oud',
     price: 199,
     imageUrl: '/images/air-shot-oud.jpg'
   }
 ];
  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`); // Use navigate function for redirection
  };

  return (
    <div className="homepage">
      
      <Banner />
      <section className="products-section">
        <h2>Featured Perfumes</h2>
        <div className="products-grid">
          {products.map((product) => (
            <ProductCard key={product.id} product={product} onClick={() => handleProductClick(product.id)} />
          ))}
        </div>
      </section>
      <Footer />
    </div>
  );
};

export default HomePage;
