// src/pages/ProductPage.js
import React, { useState, useEffect } from 'react';
import StarRatings from 'react-star-ratings';
import { FacebookShareButton, TwitterShareButton, WhatsappShareButton } from 'react-share';
import { FaFacebook, FaTwitter, FaWhatsapp, FaShareAlt } from 'react-icons/fa';
import './ProductPage.css';

const ProductPage = () => {
  const [product, setProduct] = useState(null);
  const [showShareOptions, setShowShareOptions] = useState(false);
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({ username: '', rating: 1, comment: '' });
  const [submissionMessage, setSubmissionMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const [additionalProducts, setAdditionalProducts] = useState([]);
  const productId = 1; // Replace with dynamic ID from URL

  const shareUrl = window.location.href;

  const toggleShareOptions = () => {
    setShowShareOptions((prev) => !prev);
  };

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(`/api/products/${productId}`);
        if (!response.ok) throw new Error('Failed to fetch product');
        const data = await response.json();
        setProduct(data);
      } catch (error) {
        console.error('Error fetching product:', error);
      }
    };

    const fetchReviews = async () => {
      try {
        const response = await fetch(`/api/products/${productId}/reviews`);
        if (!response.ok) throw new Error('Failed to fetch reviews');
        const data = await response.json();
        setReviews(data);
      } catch (error) {
        console.error('Error fetching reviews:', error);
      }
    };

    const fetchAdditionalProducts = async () => {
      try {
        const response = await fetch(`/api/products/recommended`);
        if (!response.ok) throw new Error('Failed to fetch additional products');
        const data = await response.json();
        setAdditionalProducts(data);
      } catch (error) {
        console.error('Error fetching additional products:', error);
      }
    };

    fetchProduct();
    fetchReviews();
    fetchAdditionalProducts();
  }, [productId]);

  const handleReviewSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch(`/api/products/${productId}/reviews`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newReview),
      });

      if (!response.ok) {
        setIsError(true);
        throw new Error('Failed to submit review');
      }

      const data = await response.json();
      setReviews((prev) => [...prev, data]);
      setSubmissionMessage('Review submitted successfully!');
      setNewReview({ username: '', rating: 1, comment: '' });
    } catch (error) {
      console.error('Error submitting review:', error);
      setSubmissionMessage('Error submitting review. Please try again.');
    }
  };

  if (!product) {
    return <div>Loading...</div>;
  }

  return (
    <div className="product-page">
      <h1>{product.name}</h1>
      <img src={`http://localhost:5000${product.imageUrl}`} alt={product.name} />
      <h2>Price: Rs. {product.price}</h2>
      <StarRatings
        rating={product.rating}
        starRatedColor="gold"
        numberOfStars={5}
        name="rating"
        starDimension="20px"
        starSpacing="3px"
      />
      <p>{product.description}</p>
      <div className="share-section">
        <button onClick={toggleShareOptions}>
          <FaShareAlt /> Share
        </button>
        {showShareOptions && (
          <div className="share-buttons">
            <FacebookShareButton url={shareUrl}>
              <FaFacebook />
            </FacebookShareButton>
            <TwitterShareButton url={shareUrl}>
              <FaTwitter />
            </TwitterShareButton>
            <WhatsappShareButton url={shareUrl}>
              <FaWhatsapp />
            </WhatsappShareButton>
          </div>
        )}
      </div>
      <h3>Reviews</h3>
      <form onSubmit={handleReviewSubmit}>
        <input
          type="text"
          placeholder="Your Name"
          value={newReview.username}
          onChange={(e) => setNewReview({ ...newReview, username: e.target.value })}
          required
        />
        <select
          value={newReview.rating}
          onChange={(e) => setNewReview({ ...newReview, rating: Number(e.target.value) })}
          required
        >
          {[1, 2, 3, 4, 5].map((star) => (
            <option key={star} value={star}>
              {star} Star{star > 1 ? 's' : ''}
            </option>
          ))}
        </select>
        <textarea
          placeholder="Your Review"
          value={newReview.comment}
          onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
          required
        />
        <button type="submit">Submit Review</button>
      </form>
      {submissionMessage && <p className={isError ? 'error' : 'success'}>{submissionMessage}</p>}
      <h3>Additional Products</h3>
      <div className="additional-products">
        {additionalProducts.map((product) => (
          <div key={product._id} className="additional-product">
            <img src={`http://localhost:5000${product.imageUrl}`} alt={product.name} />
            <h4>{product.name}</h4>
            <p>Price: Rs. {product.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductPage;
