import React, { useState } from 'react';
import ReviewForm from './ReviewForm'; // Import the ReviewForm component

const ProductPage = ({ product }) => {
  const [reviews, setReviews] = useState({});
  const [submissionMessage, setSubmissionMessage] = useState('');
  const [isError, setIsError] = useState(false);

  const handleReviewSubmit = (productId, review) => {
    setReviews((prevReviews) => ({
      ...prevReviews,
      [productId]: prevReviews[productId] ? [...prevReviews[productId], review] : [review],
    }));

    setSubmissionMessage('Review submitted successfully!');
    setIsError(false);
  };

  return (
    <div className="product-page">
      <h1>{product.name}</h1>
      <img src={`/images/${product.imageUrl}`} alt={product.name} />
      <p>{product.description}</p>
      <p>Price: Rs. {product.price}</p>

      {/* Button to navigate to the ReviewForm */}
      <button onClick={() => handleReviewSubmit(product._id, null)}>Write a Review</button>
      
      <h2>Customer Reviews</h2>
      {reviews[product._id] && reviews[product._id].map((review, index) => (
        <div className="review" key={index}>
          <h4>{review.username}</h4>
          <div className="stars">{'★'.repeat(review.rating) + '☆'.repeat(5 - review.rating)}</div>
          <p>{review.comment}</p>
        </div>
      ))}

      {/* Show the ReviewForm component */}
      <ReviewForm onSubmit={(review) => handleReviewSubmit(product._id, review)} submissionMessage={submissionMessage} isError={isError} />
    </div>
  );
};

export default ProductPage;
